/**
 * Get table specific information from the infolist or descriptor
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableATUpdate (ObitTableAT *in, ObitErr *err)
{
  olong i;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM];
  ObitTableDesc *desc;
   union ObitInfoListEquiv InfoReal; 

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Get Keywords */
   /* revision */
  in->revision = 3; 
  ObitInfoListGetTest(in->myDesc->info, "TABREV", &type, dim, 
		       (gpointer)&in->revision);
   /* obscode */
  strncpy (in->obscode, "AA000", MAXKEYCHARTABLEAT); 
  ObitInfoListGetTest(in->myDesc->info, "OBSCODE", &type, dim, 
		       (gpointer)&in->obscode);
   /* RefDate */
  strncpy (in->RefDate, "YYYY-MM-DD", MAXKEYCHARTABLEAT); 
  ObitInfoListGetTest(in->myDesc->info, "RDATE", &type, dim, 
		       (gpointer)&in->RefDate);
   /* numStkd */
  if (!ObitInfoListGet(in->myDesc->info, "NO_STKD", &type, dim, 
		       (gpointer)&in->numStkd, err)) return;
   /* stk1 */
  if (!ObitInfoListGet(in->myDesc->info, "STK_1", &type, dim, 
		       (gpointer)&in->stk1, err)) return;
   /* numBand */
  if (!ObitInfoListGet(in->myDesc->info, "NO_BAND", &type, dim, 
		       (gpointer)&in->numBand, err)) return;
   /* numChan */
  if (!ObitInfoListGet(in->myDesc->info, "NO_CHAN", &type, dim, 
		       (gpointer)&in->numChan, err)) return;
   /* refFreq */
  if (!ObitInfoListGet(in->myDesc->info, "REF_FREQ", &type, dim, 
		       (gpointer)&InfoReal, err)) return;
  if (type==OBIT_double) in->refFreq = InfoReal.dbl;
  if (type==OBIT_float)  in->refFreq = InfoReal.flt;
   /* chanBW */
  if (!ObitInfoListGet(in->myDesc->info, "CHAN_BW", &type, dim, 
		       (gpointer)&InfoReal, err)) return;
  if (type==OBIT_double) in->chanBW = InfoReal.dbl;
  if (type==OBIT_float)  in->chanBW = InfoReal.flt;
   /* refPixl */
  if (!ObitInfoListGet(in->myDesc->info, "REF_PIXL", &type, dim, 
		       (gpointer)&InfoReal, err)) return;
  if (type==OBIT_double) in->refPixl = InfoReal.dbl;
  if (type==OBIT_float)  in->refPixl = InfoReal.flt;
   /* noPCal */
  if (!ObitInfoListGet(in->myDesc->info, "NOPCAL", &type, dim, 
		       (gpointer)&in->noPCal, err)) return;
   /* polType */
  strncpy (in->polType, "APPROX  ", MAXKEYCHARTABLEAT); 
  ObitInfoListGetTest(in->myDesc->info, "POLTYPE", &type, dim, 
		       (gpointer)&in->polType);
   /* FreqID */
  in->FreqID = 0; 
  ObitInfoListGetTest(in->myDesc->info, "FREQID", &type, dim, 
		       (gpointer)&in->FreqID);

  /* initialize column numbers/offsets */
  in->TimeOff = -1;
  in->TimeCol = -1;
  in->TimeIOff = -1;
  in->TimeICol = -1;
  in->AntNameOff = -1;
  in->AntNameCol = -1;
  in->antennaNoOff = -1;
  in->antennaNoCol = -1;
  in->ArrayOff = -1;
  in->ArrayCol = -1;
  in->numLevelsOff = -1;
  in->numLevelsCol = -1;
  in->polTypeAOff = -1;
  in->polTypeACol = -1;
  in->PolAngAOff = -1;
  in->PolAngACol = -1;
  in->PolCalAOff = -1;
  in->PolCalACol = -1;
  in->polTypeBOff = -1;
  in->polTypeBCol = -1;
  in->PolAngBOff = -1;
  in->PolAngBCol = -1;
  in->PolCalBOff = -1;
  in->PolCalBCol = -1;
  /* Find columns and set offsets */
  desc = in->myDesc;
  if (desc->FieldName) {
    for (i=0; i<desc->nfield; i++) {
      if (!strncmp (desc->FieldName[i], "TIME    ", 8)) {
	 in->TimeOff = desc->offset[i];
 	 in->TimeCol = i;
      }
      if (!strncmp (desc->FieldName[i], "TIME_INTERVAL ", 14)) {
	 in->TimeIOff = desc->offset[i];
 	 in->TimeICol = i;
      }
      if (!strncmp (desc->FieldName[i], "ANNAME  ", 8)) {
	 in->AntNameOff = desc->offset[i];
 	 in->AntNameCol = i;
      }
      if (!strncmp (desc->FieldName[i], "ANTENNA_NO", 10)) {
	 in->antennaNoOff = desc->offset[i];
 	 in->antennaNoCol = i;
      }
      if (!strncmp (desc->FieldName[i], "ARRAY      ", 11)) {
	 in->ArrayOff = desc->offset[i];
 	 in->ArrayCol = i;
      }
      if (!strncmp (desc->FieldName[i], "NO_LEVELS", 9)) {
	 in->numLevelsOff = desc->offset[i];
 	 in->numLevelsCol = i;
      }
      if (!strncmp (desc->FieldName[i], "POLTYA  ", 8)) {
	 in->polTypeAOff = desc->offset[i];
 	 in->polTypeACol = i;
      }
      if (!strncmp (desc->FieldName[i], "POLAA   ", 8)) {
	 in->PolAngAOff = desc->offset[i];
 	 in->PolAngACol = i;
      }
      if (!strncmp (desc->FieldName[i], "POLCALA", 7)) {
	 in->PolCalAOff = desc->offset[i];
 	 in->PolCalACol = i;
      }
      if (!strncmp (desc->FieldName[i], "POLTYB ", 7)) {
	 in->polTypeBOff = desc->offset[i];
 	 in->polTypeBCol = i;
      }
      if (!strncmp (desc->FieldName[i], "POLAB  ", 7)) {
	 in->PolAngBOff = desc->offset[i];
 	 in->PolAngBCol = i;
      }
      if (!strncmp (desc->FieldName[i], "POLCALB", 7)) {
	 in->PolCalBOff = desc->offset[i];
 	 in->PolCalBCol = i;
      }
     }
  }

  /* Check required columns */
  Obit_return_if_fail((in->TimeOff > -1), err,
       "ObitTableATUpdate: Could not find column Time");
  Obit_return_if_fail((in->TimeIOff > -1), err,
       "ObitTableATUpdate: Could not find column TimeI");
  Obit_return_if_fail((in->AntNameOff > -1), err,
       "ObitTableATUpdate: Could not find column AntName");
  Obit_return_if_fail((in->antennaNoOff > -1), err,
       "ObitTableATUpdate: Could not find column antennaNo");
  Obit_return_if_fail((in->ArrayOff > -1), err,
       "ObitTableATUpdate: Could not find column Array");
  Obit_return_if_fail((in->numLevelsOff > -1), err,
       "ObitTableATUpdate: Could not find column numLevels");
  Obit_return_if_fail((in->polTypeAOff > -1), err,
       "ObitTableATUpdate: Could not find column polTypeA");
  Obit_return_if_fail((in->PolAngAOff > -1), err,
       "ObitTableATUpdate: Could not find column PolAngA");
  Obit_return_if_fail((in->PolCalAOff > -1), err,
       "ObitTableATUpdate: Could not find column PolCalA");
  Obit_return_if_fail((in->polTypeBOff > -1), err,
       "ObitTableATUpdate: Could not find column polTypeB");
  Obit_return_if_fail((in->PolAngBOff > -1), err,
       "ObitTableATUpdate: Could not find column PolAngB");
} /* end ObitTableATUpdate */

/**
 * Copy table specific (keyword) information  to infolist.
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableATDumpKey (ObitTableAT *in, ObitErr *err)
{
  ObitInfoList *info=NULL;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM] = {1,1,1,1,1};

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Set Keywords */
  if (in->myIO!=NULL) info = ((ObitTableDesc*)(in->myIO->myDesc))->info;
  else info = in->myDesc->info;
  /* revision */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "TABREV", type, dim, 
		  (gpointer)&in->revision);
  /* obscode */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEAT;
  ObitInfoListAlwaysPut(info, "OBSCODE", type, dim, 
		  (gpointer)&in->obscode);
  /* RefDate */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEAT;
  ObitInfoListAlwaysPut(info, "RDATE", type, dim, 
		  (gpointer)&in->RefDate);
  /* numStkd */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_STKD", type, dim, 
		  (gpointer)&in->numStkd);
  /* stk1 */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "STK_1", type, dim, 
		  (gpointer)&in->stk1);
  /* numBand */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_BAND", type, dim, 
		  (gpointer)&in->numBand);
  /* numChan */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_CHAN", type, dim, 
		  (gpointer)&in->numChan);
  /* refFreq */
  type  = OBIT_double;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "REF_FREQ", type, dim, 
		  (gpointer)&in->refFreq);
  /* chanBW */
  type  = OBIT_float;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "CHAN_BW", type, dim, 
		  (gpointer)&in->chanBW);
  /* refPixl */
  type  = OBIT_float;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "REF_PIXL", type, dim, 
		  (gpointer)&in->refPixl);
  /* noPCal */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NOPCAL", type, dim, 
		  (gpointer)&in->noPCal);
  /* polType */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEAT;
  ObitInfoListAlwaysPut(info, "POLTYPE", type, dim, 
		  (gpointer)&in->polType);
  /* FreqID */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "FREQID", type, dim, 
		  (gpointer)&in->FreqID);
   
} /* end ObitTableATDumpKey */
