/**
 * Get table specific information from the infolist or descriptor
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableBPUpdate (ObitTableBP *in, ObitErr *err)
{
  olong i;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM];
  ObitTableDesc *desc;
   

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Get Keywords */
   /* numAnt */
  if (!ObitInfoListGet(in->myDesc->info, "NO_ANT", &type, dim, 
		       (gpointer)&in->numAnt, err)) return;
   /* numPol */
  if (!ObitInfoListGet(in->myDesc->info, "NO_POL", &type, dim, 
		       (gpointer)&in->numPol, err)) return;
   /* numIF */
  if (!ObitInfoListGet(in->myDesc->info, "NO_IF", &type, dim, 
		       (gpointer)&in->numIF, err)) return;
   /* numChan */
  if (!ObitInfoListGet(in->myDesc->info, "NO_CHAN", &type, dim, 
		       (gpointer)&in->numChan, err)) return;
   /* startChan */
  in->startChan = 1; 
  ObitInfoListGetTest(in->myDesc->info, "STRT_CHN", &type, dim, 
		       (gpointer)&in->startChan);
   /* numShifts */
  in->numShifts = 1; 
  ObitInfoListGetTest(in->myDesc->info, "NO_SHFTS", &type, dim, 
		       (gpointer)&in->numShifts);
   /* lowShift */
  in->lowShift = 1; 
  ObitInfoListGetTest(in->myDesc->info, "LOW_SHFT", &type, dim, 
		       (gpointer)&in->lowShift);
   /* shiftInc */
  in->shiftInc = 1; 
  ObitInfoListGetTest(in->myDesc->info, "SHFT_INC", &type, dim, 
		       (gpointer)&in->shiftInc);
   /* BPType */
  if (!ObitInfoListGet(in->myDesc->info, "BP_TYPE", &type, dim, 
		       (gpointer)&in->BPType, err)) return;

  /* initialize column numbers/offsets */
  in->TimeOff = -1;
  in->TimeCol = -1;
  in->TimeIOff = -1;
  in->TimeICol = -1;
  in->SourIDOff = -1;
  in->SourIDCol = -1;
  in->SubAOff = -1;
  in->SubACol = -1;
  in->antNoOff = -1;
  in->antNoCol = -1;
  in->BWOff = -1;
  in->BWCol = -1;
  in->ChanShiftOff = -1;
  in->ChanShiftCol = -1;
  in->FreqIDOff = -1;
  in->FreqIDCol = -1;
  in->RefAnt1Off = -1;
  in->RefAnt1Col = -1;
  in->Weight1Off = -1;
  in->Weight1Col = -1;
  in->Real1Off = -1;
  in->Real1Col = -1;
  in->Imag1Off = -1;
  in->Imag1Col = -1;
  in->RefAnt2Off = -1;
  in->RefAnt2Col = -1;
  in->Weight2Off = -1;
  in->Weight2Col = -1;
  in->Real2Off = -1;
  in->Real2Col = -1;
  in->Imag2Off = -1;
  in->Imag2Col = -1;
  /* Find columns and set offsets */
  desc = in->myDesc;
  if (desc->FieldName) {
    for (i=0; i<desc->nfield; i++) {
      if (!strncmp (desc->FieldName[i], "TIME    ", 8)) {
	 in->TimeOff = desc->offset[i];
 	 in->TimeCol = i;
      }
      if (!strncmp (desc->FieldName[i], "INTERVAL ", 9)) {
	 in->TimeIOff = desc->offset[i];
 	 in->TimeICol = i;
      }
      if (!strncmp (desc->FieldName[i], "SOURCE ID ", 10)) {
	 in->SourIDOff = desc->offset[i];
 	 in->SourIDCol = i;
      }
      if (!strncmp (desc->FieldName[i], "SUBARRAY ", 9)) {
	 in->SubAOff = desc->offset[i];
 	 in->SubACol = i;
      }
      if (!strncmp (desc->FieldName[i], "ANTENNA ", 8)) {
	 in->antNoOff = desc->offset[i];
 	 in->antNoCol = i;
      }
      if (!strncmp (desc->FieldName[i], "BANDWIDTH ", 10)) {
	 in->BWOff = desc->offset[i];
 	 in->BWCol = i;
      }
      if (!strncmp (desc->FieldName[i], "CHN_SHIFT ", 10)) {
	 in->ChanShiftOff = desc->offset[i];
 	 in->ChanShiftCol = i;
      }
      if (!strncmp (desc->FieldName[i], "FREQ ID ", 8)) {
	 in->FreqIDOff = desc->offset[i];
 	 in->FreqIDCol = i;
      }
      if (!strncmp (desc->FieldName[i], "REFANT 1", 8)) {
	     in->RefAnt1Off = desc->offset[i];
 	     in->RefAnt1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "WEIGHT 1", 8)) {
	     in->Weight1Off = desc->offset[i];
 	     in->Weight1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "REAL 1", 6)) {
	     in->Real1Off = desc->offset[i];
 	     in->Real1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "IMAG 1", 6)) {
	     in->Imag1Off = desc->offset[i];
 	     in->Imag1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "REFANT 2", 8)) {
	     in->RefAnt2Off = desc->offset[i];
 	     in->RefAnt2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "WEIGHT 2", 8)) {
	     in->Weight2Off = desc->offset[i];
 	     in->Weight2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "REAL 2", 6)) {
	     in->Real2Off = desc->offset[i];
 	     in->Real2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "IMAG 2", 6)) {
	     in->Imag2Off = desc->offset[i];
 	     in->Imag2Col = i;
      }
     }
  }

  /* Check required columns */
  Obit_return_if_fail((in->TimeOff > -1), err,
       "ObitTableBPUpdate: Could not find column Time");
  Obit_return_if_fail((in->TimeIOff > -1), err,
       "ObitTableBPUpdate: Could not find column TimeI");
  Obit_return_if_fail((in->SourIDOff > -1), err,
       "ObitTableBPUpdate: Could not find column SourID");
  Obit_return_if_fail((in->SubAOff > -1), err,
       "ObitTableBPUpdate: Could not find column SubA");
  Obit_return_if_fail((in->antNoOff > -1), err,
       "ObitTableBPUpdate: Could not find column antNo");
  Obit_return_if_fail((in->BWOff > -1), err,
       "ObitTableBPUpdate: Could not find column BW");
  Obit_return_if_fail((in->ChanShiftOff > -1), err,
       "ObitTableBPUpdate: Could not find column ChanShift");
  Obit_return_if_fail((in->FreqIDOff > -1), err,
       "ObitTableBPUpdate: Could not find column FreqID");
  if (in->numPol>=1) {
    Obit_return_if_fail((in->RefAnt1Off > -1), err,
       "ObitTableBPUpdate: Could not find column RefAnt1");
    Obit_return_if_fail((in->Weight1Off > -1), err,
       "ObitTableBPUpdate: Could not find column Weight1");
    Obit_return_if_fail((in->Real1Off > -1), err,
       "ObitTableBPUpdate: Could not find column Real1");
  } /* end of if index in range */
  if (in->numPol>=2) {
    Obit_return_if_fail((in->RefAnt2Off > -1), err,
       "ObitTableBPUpdate: Could not find column RefAnt2");
    Obit_return_if_fail((in->Weight2Off > -1), err,
       "ObitTableBPUpdate: Could not find column Weight2");
    Obit_return_if_fail((in->Real2Off > -1), err,
       "ObitTableBPUpdate: Could not find column Real2");
  } /* end of if index in range */
} /* end ObitTableBPUpdate */

/**
 * Copy table specific (keyword) information  to infolist.
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableBPDumpKey (ObitTableBP *in, ObitErr *err)
{
  ObitInfoList *info=NULL;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM] = {1,1,1,1,1};

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Set Keywords */
  if (in->myIO!=NULL) info = ((ObitTableDesc*)(in->myIO->myDesc))->info;
  else info = in->myDesc->info;
  /* numAnt */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_ANT", type, dim, 
		  (gpointer)&in->numAnt);
  /* numPol */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_POL", type, dim, 
		  (gpointer)&in->numPol);
  /* numIF */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_IF", type, dim, 
		  (gpointer)&in->numIF);
  /* numChan */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_CHAN", type, dim, 
		  (gpointer)&in->numChan);
  /* startChan */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "STRT_CHN", type, dim, 
		  (gpointer)&in->startChan);
  /* numShifts */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_SHFTS", type, dim, 
		  (gpointer)&in->numShifts);
  /* lowShift */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "LOW_SHFT", type, dim, 
		  (gpointer)&in->lowShift);
  /* shiftInc */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "SHFT_INC", type, dim, 
		  (gpointer)&in->shiftInc);
  /* BPType */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEBP;
  ObitInfoListAlwaysPut(info, "BP_TYPE", type, dim, 
		  (gpointer)&in->BPType);
   
} /* end ObitTableBPDumpKey */
