/**
 * Get table specific information from the infolist or descriptor
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableCQUpdate (ObitTableCQ *in, ObitErr *err)
{
  olong i;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM];
  ObitTableDesc *desc;
   

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Get Keywords */
   /* revision */
  in->revision = 1; 
  ObitInfoListGetTest(in->myDesc->info, "TABREV", &type, dim, 
		       (gpointer)&in->revision);
   /* numIF */
  if (!ObitInfoListGet(in->myDesc->info, "NO_IF", &type, dim, 
		       (gpointer)&in->numIF, err)) return;

  /* initialize column numbers/offsets */
  in->FrqSelOff = -1;
  in->FrqSelCol = -1;
  in->SubAOff = -1;
  in->SubACol = -1;
  in->FFTSizeOff = -1;
  in->FFTSizeCol = -1;
  in->numChanOff = -1;
  in->numChanCol = -1;
  in->SpecAvgOff = -1;
  in->SpecAvgCol = -1;
  in->EdgeFreqOff = -1;
  in->EdgeFreqCol = -1;
  in->ChanBWOff = -1;
  in->ChanBWCol = -1;
  in->TaperFnOff = -1;
  in->TaperFnCol = -1;
  in->OverSampOff = -1;
  in->OverSampCol = -1;
  in->ZeroPadOff = -1;
  in->ZeroPadCol = -1;
  in->FilterOff = -1;
  in->FilterCol = -1;
  in->TimeAvgOff = -1;
  in->TimeAvgCol = -1;
  in->numBitsOff = -1;
  in->numBitsCol = -1;
  in->FFTOverlapOff = -1;
  in->FFTOverlapCol = -1;
  /* Find columns and set offsets */
  desc = in->myDesc;
  if (desc->FieldName) {
    for (i=0; i<desc->nfield; i++) {
      if (!strncmp (desc->FieldName[i], "FRQSEL ", 7)) {
	 in->FrqSelOff = desc->offset[i];
 	 in->FrqSelCol = i;
      }
      if (!strncmp (desc->FieldName[i], "SUBARRAY ", 9)) {
	 in->SubAOff = desc->offset[i];
 	 in->SubACol = i;
      }
      if (!strncmp (desc->FieldName[i], "FFT_SIZE ", 9)) {
	 in->FFTSizeOff = desc->offset[i];
 	 in->FFTSizeCol = i;
      }
      if (!strncmp (desc->FieldName[i], "NO_CHAN ", 8)) {
	 in->numChanOff = desc->offset[i];
 	 in->numChanCol = i;
      }
      if (!strncmp (desc->FieldName[i], "SPEC_AVG ", 9)) {
	 in->SpecAvgOff = desc->offset[i];
 	 in->SpecAvgCol = i;
      }
      if (!strncmp (desc->FieldName[i], "EDGE_FRQ ", 9)) {
	 in->EdgeFreqOff = desc->offset[i];
 	 in->EdgeFreqCol = i;
      }
      if (!strncmp (desc->FieldName[i], "CHAN_BW ", 8)) {
	 in->ChanBWOff = desc->offset[i];
 	 in->ChanBWCol = i;
      }
      if (!strncmp (desc->FieldName[i], "TAPER_FN ", 9)) {
	 in->TaperFnOff = desc->offset[i];
 	 in->TaperFnCol = i;
      }
      if (!strncmp (desc->FieldName[i], "OVR_SAMP ", 9)) {
	 in->OverSampOff = desc->offset[i];
 	 in->OverSampCol = i;
      }
      if (!strncmp (desc->FieldName[i], "ZERO_PAD ", 9)) {
	 in->ZeroPadOff = desc->offset[i];
 	 in->ZeroPadCol = i;
      }
      if (!strncmp (desc->FieldName[i], "FILTER ", 7)) {
	 in->FilterOff = desc->offset[i];
 	 in->FilterCol = i;
      }
      if (!strncmp (desc->FieldName[i], "TIME_AVG ", 9)) {
	 in->TimeAvgOff = desc->offset[i];
 	 in->TimeAvgCol = i;
      }
      if (!strncmp (desc->FieldName[i], "NO_BITS ", 8)) {
	 in->numBitsOff = desc->offset[i];
 	 in->numBitsCol = i;
      }
      if (!strncmp (desc->FieldName[i], "FFT_OVLP ", 9)) {
	 in->FFTOverlapOff = desc->offset[i];
 	 in->FFTOverlapCol = i;
      }
     }
  }

  /* Check required columns */
  Obit_return_if_fail((in->FrqSelOff > -1), err,
       "ObitTableCQUpdate: Could not find column FrqSel");
  Obit_return_if_fail((in->SubAOff > -1), err,
       "ObitTableCQUpdate: Could not find column SubA");
  Obit_return_if_fail((in->FFTSizeOff > -1), err,
       "ObitTableCQUpdate: Could not find column FFTSize");
  Obit_return_if_fail((in->numChanOff > -1), err,
       "ObitTableCQUpdate: Could not find column numChan");
  Obit_return_if_fail((in->SpecAvgOff > -1), err,
       "ObitTableCQUpdate: Could not find column SpecAvg");
  Obit_return_if_fail((in->EdgeFreqOff > -1), err,
       "ObitTableCQUpdate: Could not find column EdgeFreq");
  Obit_return_if_fail((in->ChanBWOff > -1), err,
       "ObitTableCQUpdate: Could not find column ChanBW");
  Obit_return_if_fail((in->TaperFnOff > -1), err,
       "ObitTableCQUpdate: Could not find column TaperFn");
  Obit_return_if_fail((in->OverSampOff > -1), err,
       "ObitTableCQUpdate: Could not find column OverSamp");
  Obit_return_if_fail((in->ZeroPadOff > -1), err,
       "ObitTableCQUpdate: Could not find column ZeroPad");
  Obit_return_if_fail((in->FilterOff > -1), err,
       "ObitTableCQUpdate: Could not find column Filter");
  Obit_return_if_fail((in->TimeAvgOff > -1), err,
       "ObitTableCQUpdate: Could not find column TimeAvg");
  Obit_return_if_fail((in->numBitsOff > -1), err,
       "ObitTableCQUpdate: Could not find column numBits");
} /* end ObitTableCQUpdate */

/**
 * Copy table specific (keyword) information  to infolist.
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableCQDumpKey (ObitTableCQ *in, ObitErr *err)
{
  ObitInfoList *info=NULL;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM] = {1,1,1,1,1};

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Set Keywords */
  if (in->myIO!=NULL) info = ((ObitTableDesc*)(in->myIO->myDesc))->info;
  else info = in->myDesc->info;
  /* revision */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "TABREV", type, dim, 
		  (gpointer)&in->revision);
  /* numIF */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_IF", type, dim, 
		  (gpointer)&in->numIF);
   
} /* end ObitTableCQDumpKey */
