ObitIOCode 
ObitTableFQWriteRow  (ObitTableFQ *in, olong iFQRow, ObitTableFQRow *row,
		      ObitErr *err)
{
  ObitIOCode retCode = OBIT_IO_SpecErr;
  gshort    *siRow;
  odouble   *dRow;
  oint      *iRow, i;
  ofloat    *fRow;
  gchar     *cRow;
  gboolean  *lRow;
  guint8    *bRow;
  gchar *routine = "ObitTableFQWriteRow";
  

  /* error checks */
  g_assert (ObitErrIsA(err));
  if (err->error) return retCode;
  g_assert (ObitIsA(in, &myClassInfo));

  if (in->myStatus == OBIT_Inactive) {
    Obit_log_error(err, OBIT_Error,
		   "AIPS FQ Table is inactive for %s ", in->name);
    return retCode;
 }

  /* Typed pointers to row of data */  
  dRow  = (odouble*)in->buffer;
  siRow = (gshort*)in->buffer;
  iRow  = (oint*)in->buffer;
  fRow  = (ofloat*)in->buffer;
  cRow  = (gchar*)in->buffer;
  lRow  = (gboolean*)in->buffer;
  bRow  = (guint8*)in->buffer;
  
  /* Make full copy of all data */
  iRow[in->fqidOff] = row->fqid;
  if (in->freqOffCol >= 0) { 
    for (i=0; i<in->myDesc->repeat[in->freqOffCol]; i++) 
      dRow[in->freqOffOff+i] = row->freqOff[i];
  } 
  if (in->chWidthCol >= 0) { 
    for (i=0; i<in->myDesc->repeat[in->chWidthCol]; i++) 
      fRow[in->chWidthOff+i] = row->chWidth[i];
  } 
  if (in->totBWCol >= 0) { 
    for (i=0; i<in->myDesc->repeat[in->totBWCol]; i++) 
      fRow[in->totBWOff+i] = row->totBW[i];
  } 
  if (in->sideBandCol >= 0) { 
    for (i=0; i<in->myDesc->repeat[in->sideBandCol]; i++) 
      iRow[in->sideBandOff+i] = row->sideBand[i];
  } 
  if (in->RxCodeCol >= 0) { 
    for (i=0; i<in->myDesc->repeat[in->RxCodeCol]; i++) 
      cRow[in->RxCodeOff+i] = row->RxCode[i];
  } 

  /* copy status */
  iRow[in->myDesc->statusOff] = row->status;
   
  /* Write one row */
  in->myDesc->numRowBuff = 1;
 
  /* Write row iFQRow */
  retCode = ObitTableWrite ((ObitTable*)in, iFQRow, NULL,  err);
  if (err->error) 
    Obit_traceback_val (err, routine,in->name, retCode);

  return retCode;
} /*  end ObitTableFQWriteRow */

/**
 * Shutdown I/O.
 * \param in Pointer to object to be closed.
 * \param err ObitErr for reporting errors.
 * \return error code, OBIT_IO_OK=> OK
 */
ObitIOCode ObitTableFQClose (ObitTableFQ *in, ObitErr *err)
{
  ObitIOCode retCode = OBIT_IO_SpecErr;
  gchar *routine = "ObitTableFQClose";

  /* error checks */
  g_assert (ObitErrIsA(err));
  if (err->error) return retCode;
  g_assert (ObitIsA((Obit*)in, &myClassInfo));
  /* Something going on? */
  if (in->myStatus == OBIT_Inactive) return OBIT_IO_OK;

  /* Update keywords on descriptor if not ReadOnly*/
  if (in->myDesc->access != OBIT_IO_ReadOnly) 
    ObitTableFQDumpKey (in, err);
  if (err->error) 
    Obit_traceback_val (err, routine, in->name, retCode);

  /* Close */
  retCode = ObitTableClose ((ObitTable*)in, err);
  if (err->error) 
    Obit_traceback_val (err, routine, in->name, retCode);

  return retCode;
} /* end ObitTableFQClose */

