/**
 * Get table specific information from the infolist or descriptor
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableFQUpdate (ObitTableFQ *in, ObitErr *err)
{
  olong i;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM];
  ObitTableDesc *desc;
   

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Get Keywords */
   /* numIF */
  if (!ObitInfoListGet(in->myDesc->info, "NO_IF", &type, dim, 
		       (gpointer)&in->numIF, err)) return;

  /* initialize column numbers/offsets */
  in->fqidOff = -1;
  in->fqidCol = -1;
  in->freqOffOff = -1;
  in->freqOffCol = -1;
  in->chWidthOff = -1;
  in->chWidthCol = -1;
  in->totBWOff = -1;
  in->totBWCol = -1;
  in->sideBandOff = -1;
  in->sideBandCol = -1;
  in->RxCodeOff = -1;
  in->RxCodeCol = -1;
  /* Find columns and set offsets */
  desc = in->myDesc;
  if (desc->FieldName) {
    for (i=0; i<desc->nfield; i++) {
      if (!strncmp (desc->FieldName[i], "FRQSEL  ", 8)) {
	 in->fqidOff = desc->offset[i];
 	 in->fqidCol = i;
      }
      if (!strncmp (desc->FieldName[i], "IF FREQ ", 8)) {
	 in->freqOffOff = desc->offset[i];
 	 in->freqOffCol = i;
      }
      if (!strncmp (desc->FieldName[i], "CH WIDTH", 8)) {
	 in->chWidthOff = desc->offset[i];
 	 in->chWidthCol = i;
      }
      if (!strncmp (desc->FieldName[i], "TOTAL BANDWIDTH ", 16)) {
	 in->totBWOff = desc->offset[i];
 	 in->totBWCol = i;
      }
      if (!strncmp (desc->FieldName[i], "SIDEBAND ", 9)) {
	 in->sideBandOff = desc->offset[i];
 	 in->sideBandCol = i;
      }
      if (!strncmp (desc->FieldName[i], "RXCODE  ", 8)) {
	 in->RxCodeOff = desc->offset[i];
 	 in->RxCodeCol = i;
      }
     }
  }

  /* Check required columns */
  Obit_return_if_fail((in->fqidOff > -1), err,
       "ObitTableFQUpdate: Could not find column fqid");
  Obit_return_if_fail((in->freqOffOff > -1), err,
       "ObitTableFQUpdate: Could not find column freqOff");
  Obit_return_if_fail((in->chWidthOff > -1), err,
       "ObitTableFQUpdate: Could not find column chWidth");
  Obit_return_if_fail((in->totBWOff > -1), err,
       "ObitTableFQUpdate: Could not find column totBW");
  Obit_return_if_fail((in->sideBandOff > -1), err,
       "ObitTableFQUpdate: Could not find column sideBand");
} /* end ObitTableFQUpdate */

/**
 * Copy table specific (keyword) information  to infolist.
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableFQDumpKey (ObitTableFQ *in, ObitErr *err)
{
  ObitInfoList *info=NULL;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM] = {1,1,1,1,1};

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Set Keywords */
  if (in->myIO!=NULL) info = ((ObitTableDesc*)(in->myIO->myDesc))->info;
  else info = in->myDesc->info;
  /* numIF */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_IF", type, dim, 
		  (gpointer)&in->numIF);
   
} /* end ObitTableFQDumpKey */
