/**
 * Get table specific information from the infolist or descriptor
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableGCUpdate (ObitTableGC *in, ObitErr *err)
{
  olong i;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM];
  ObitTableDesc *desc;
   

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Get Keywords */
   /* numBand */
  if (!ObitInfoListGet(in->myDesc->info, "NO_BAND", &type, dim, 
		       (gpointer)&in->numBand, err)) return;
   /* numPol */
  if (!ObitInfoListGet(in->myDesc->info, "NO_POL", &type, dim, 
		       (gpointer)&in->numPol, err)) return;
   /* numTabs */
  if (!ObitInfoListGet(in->myDesc->info, "NO_TABS", &type, dim, 
		       (gpointer)&in->numTabs, err)) return;
   /* revision */
  in->revision = 3; 
  ObitInfoListGetTest(in->myDesc->info, "TABREV", &type, dim, 
		       (gpointer)&in->revision);

  /* initialize column numbers/offsets */
  in->antennaNoOff = -1;
  in->antennaNoCol = -1;
  in->SubArrayOff = -1;
  in->SubArrayCol = -1;
  in->FreqIDOff = -1;
  in->FreqIDCol = -1;
  in->Type1Off = -1;
  in->Type1Col = -1;
  in->NTerm1Off = -1;
  in->NTerm1Col = -1;
  in->XTyp1Off = -1;
  in->XTyp1Col = -1;
  in->YTyp1Off = -1;
  in->YTyp1Col = -1;
  in->XVal1Off = -1;
  in->XVal1Col = -1;
  in->YVal1Off = -1;
  in->YVal1Col = -1;
  in->gain1Off = -1;
  in->gain1Col = -1;
  in->sens1Off = -1;
  in->sens1Col = -1;
  in->Type2Off = -1;
  in->Type2Col = -1;
  in->NTerm2Off = -1;
  in->NTerm2Col = -1;
  in->XTyp2Off = -1;
  in->XTyp2Col = -1;
  in->YTyp2Off = -1;
  in->YTyp2Col = -1;
  in->XVal2Off = -1;
  in->XVal2Col = -1;
  in->YVal2Off = -1;
  in->YVal2Col = -1;
  in->gain2Off = -1;
  in->gain2Col = -1;
  in->sens2Off = -1;
  in->sens2Col = -1;
  /* Find columns and set offsets */
  desc = in->myDesc;
  if (desc->FieldName) {
    for (i=0; i<desc->nfield; i++) {
      if (!strncmp (desc->FieldName[i], "ANTENNA_NO", 10)) {
	 in->antennaNoOff = desc->offset[i];
 	 in->antennaNoCol = i;
      }
      if (!strncmp (desc->FieldName[i], "SUBARRAY ", 9)) {
	 in->SubArrayOff = desc->offset[i];
 	 in->SubArrayCol = i;
      }
      if (!strncmp (desc->FieldName[i], "FREQ ID  ", 9)) {
	 in->FreqIDOff = desc->offset[i];
 	 in->FreqIDCol = i;
      }
      if (!strncmp (desc->FieldName[i], "TYPE_1", 6)) {
	     in->Type1Off = desc->offset[i];
 	     in->Type1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "NTERM_1", 7)) {
	     in->NTerm1Off = desc->offset[i];
 	     in->NTerm1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "X_TYP_1", 7)) {
	     in->XTyp1Off = desc->offset[i];
 	     in->XTyp1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "Y_TYP_1", 7)) {
	     in->YTyp1Off = desc->offset[i];
 	     in->YTyp1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "X_VAL_1", 7)) {
	     in->XVal1Off = desc->offset[i];
 	     in->XVal1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "Y_VAL_1", 7)) {
	     in->YVal1Off = desc->offset[i];
 	     in->YVal1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "GAIN_1", 6)) {
	     in->gain1Off = desc->offset[i];
 	     in->gain1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "SENS_1", 6)) {
	     in->sens1Off = desc->offset[i];
 	     in->sens1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "TYPE_2", 6)) {
	     in->Type2Off = desc->offset[i];
 	     in->Type2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "NTERM_2", 7)) {
	     in->NTerm2Off = desc->offset[i];
 	     in->NTerm2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "X_TYP_2", 7)) {
	     in->XTyp2Off = desc->offset[i];
 	     in->XTyp2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "Y_TYP_2", 7)) {
	     in->YTyp2Off = desc->offset[i];
 	     in->YTyp2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "X_VAL_2", 7)) {
	     in->XVal2Off = desc->offset[i];
 	     in->XVal2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "Y_VAL_2", 7)) {
	     in->YVal2Off = desc->offset[i];
 	     in->YVal2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "GAIN_2", 6)) {
	     in->gain2Off = desc->offset[i];
 	     in->gain2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "SENS_2", 6)) {
	     in->sens2Off = desc->offset[i];
 	     in->sens2Col = i;
      }
     }
  }

  /* Check required columns */
  Obit_return_if_fail((in->antennaNoOff > -1), err,
       "ObitTableGCUpdate: Could not find column antennaNo");
  Obit_return_if_fail((in->SubArrayOff > -1), err,
       "ObitTableGCUpdate: Could not find column SubArray");
  Obit_return_if_fail((in->FreqIDOff > -1), err,
       "ObitTableGCUpdate: Could not find column FreqID");
  if (in->numPol>=1) {
    Obit_return_if_fail((in->Type1Off > -1), err,
       "ObitTableGCUpdate: Could not find column Type1");
    Obit_return_if_fail((in->NTerm1Off > -1), err,
       "ObitTableGCUpdate: Could not find column NTerm1");
    Obit_return_if_fail((in->XTyp1Off > -1), err,
       "ObitTableGCUpdate: Could not find column XTyp1");
    Obit_return_if_fail((in->YTyp1Off > -1), err,
       "ObitTableGCUpdate: Could not find column YTyp1");
    Obit_return_if_fail((in->XVal1Off > -1), err,
       "ObitTableGCUpdate: Could not find column XVal1");
    Obit_return_if_fail((in->YVal1Off > -1), err,
       "ObitTableGCUpdate: Could not find column YVal1");
    Obit_return_if_fail((in->gain1Off > -1), err,
       "ObitTableGCUpdate: Could not find column gain1");
  } /* end of if index in range */
  if (in->numPol>=2) {
    Obit_return_if_fail((in->Type2Off > -1), err,
       "ObitTableGCUpdate: Could not find column Type2");
    Obit_return_if_fail((in->NTerm2Off > -1), err,
       "ObitTableGCUpdate: Could not find column NTerm2");
    Obit_return_if_fail((in->XTyp2Off > -1), err,
       "ObitTableGCUpdate: Could not find column XTyp2");
    Obit_return_if_fail((in->YTyp2Off > -1), err,
       "ObitTableGCUpdate: Could not find column YTyp2");
    Obit_return_if_fail((in->XVal2Off > -1), err,
       "ObitTableGCUpdate: Could not find column XVal2");
    Obit_return_if_fail((in->YVal2Off > -1), err,
       "ObitTableGCUpdate: Could not find column YVal2");
    Obit_return_if_fail((in->gain2Off > -1), err,
       "ObitTableGCUpdate: Could not find column gain2");
  } /* end of if index in range */
} /* end ObitTableGCUpdate */

/**
 * Copy table specific (keyword) information  to infolist.
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableGCDumpKey (ObitTableGC *in, ObitErr *err)
{
  ObitInfoList *info=NULL;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM] = {1,1,1,1,1};

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Set Keywords */
  if (in->myIO!=NULL) info = ((ObitTableDesc*)(in->myIO->myDesc))->info;
  else info = in->myDesc->info;
  /* numBand */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_BAND", type, dim, 
		  (gpointer)&in->numBand);
  /* numPol */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_POL", type, dim, 
		  (gpointer)&in->numPol);
  /* numTabs */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_TABS", type, dim, 
		  (gpointer)&in->numTabs);
  /* revision */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "TABREV", type, dim, 
		  (gpointer)&in->revision);
   
} /* end ObitTableGCDumpKey */
