ObitIOCode 
ObitTableIDI_ANTENNAReadRow  (ObitTableIDI_ANTENNA *in, olong iIDI_ANTENNARow, ObitTableIDI_ANTENNARow *row,
		     ObitErr *err)
{
  ObitIOCode retCode = OBIT_IO_SpecErr;
  odouble   *dRow;
  oint      *iRow;
  gshort    *siRow;
  ofloat    *fRow;
  gchar     *cRow;
  gboolean  *lRow;
  guint8    *bRow;
  gchar *routine = "ObitTableIDI_ANTENNAReadRow";
  
  /* error checks */
  g_assert (ObitErrIsA(err));
  if (err->error) return retCode;
  g_assert (ObitIsA(in, &myClassInfo));

  if (in->myStatus == OBIT_Inactive) {
    Obit_log_error(err, OBIT_Error,
		   "IDI_ANTENNA Table is inactive for  %s ", in->name);
    return retCode;
 }

  /* read row iIDI_ANTENNARow */
  retCode = ObitTableRead ((ObitTable*)in, iIDI_ANTENNARow, NULL,  err);
  if (err->error) 
    Obit_traceback_val (err, routine, in->name, retCode);

  /* Typed pointers to row of data */  
  dRow  = (odouble*)in->buffer;
  iRow  = (oint*)in->buffer;
  siRow = (gshort*)in->buffer;
  fRow  = (ofloat*)in->buffer;
  cRow  = (gchar*)in->buffer;
  lRow  = (gboolean*)in->buffer;
  bRow  = (guint8*)in->buffer;
  
  /* Copy scalar fields, for arrays only set pointer*/
  row->time = dRow[in->timeOff];
  row->time_interval = fRow[in->time_intervalOff];
  row->antenna_no = iRow[in->antenna_noOff];
  row->array = iRow[in->arrayOff];
  row->freqid = iRow[in->freqidOff];
  row->no_levels = iRow[in->no_levelsOff];
  row->PolAngA = fRow[in->PolAngAOff];
  row->PolAngB = fRow[in->PolAngBOff];
  row->AntName = cRow + in->AntNameOff;
  row->polTypeA = cRow + in->polTypeAOff;
  row->PolCalA = fRow + in->PolCalAOff;
  row->polTypeB = cRow + in->polTypeBOff;
  row->PolCalB = fRow + in->PolCalBOff;
  row->status = iRow[in->myDesc->statusOff];

  return retCode;
} /*  end ObitTableIDI_ANTENNAReadRow */

/**
 * Attach an ObitTableRow to the buffer of an ObitTable.
 * This is only useful prior to filling a row structure in preparation .
 * for a WriteRow operation.  Array members of the Row structure are .
 * pointers to independently allocated memory, this routine allows using .
 * the table IO buffer instead of allocating yet more memory..
 * This routine need only be called once to initialize a Row structure for write..
 * \param in  Table with buffer to be written 
 * \param row Table Row structure to attach 
 * \param err ObitErr for reporting errors.
 */
void 
ObitTableIDI_ANTENNASetRow  (ObitTableIDI_ANTENNA *in, ObitTableIDI_ANTENNARow *row,
		     ObitErr *err)
{
  odouble   *dRow;
  oint      *iRow;
  gshort    *siRow;
  ofloat    *fRow;
  gchar     *cRow;
  gboolean  *lRow;
  guint8    *bRow;
  
  /* error checks */
  g_assert (ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));
  g_assert (ObitIsA(row, &myRowClassInfo));

  if (in->myStatus == OBIT_Inactive) {
    Obit_log_error(err, OBIT_Error,
		   "IDI_ANTENNA Table is inactive for  %s ", in->name);
    return;
 }

  /* Typed pointers to row of data */  
  dRow  = (odouble*)in->buffer;
  iRow  = (oint*)in->buffer;
  siRow = (gshort*)in->buffer;
  fRow  = (ofloat*)in->buffer;
  cRow  = (gchar*)in->buffer;
  lRow  = (gboolean*)in->buffer;
  bRow  = (guint8*)in->buffer;
  
  /* Set row pointers to buffer */
  row->AntName = cRow + in->AntNameOff;
  row->polTypeA = cRow + in->polTypeAOff;
  row->PolCalA = fRow + in->PolCalAOff;
  row->polTypeB = cRow + in->polTypeBOff;
  row->PolCalB = fRow + in->PolCalBOff;

} /*  end ObitTableIDI_ANTENNASetRow */

/**
 * Write a table row.
 * Before calling this routine, the row structure needs to be initialized
 * and filled with data. The array members of the row structure are  
 * pointers to independently allocated memory.  These pointers can be set to the 
 * correct table buffer locations using ObitTableIDI_ANTENNASetRow  
 * \param in       Table to read
 * \param iIDI_ANTENNARow   Row number, -1 -> next
 * \param row Table Row structure containing data
 * \param err ObitErr for reporting errors.
 * \return return code, OBIT_IO_OK=> OK
 */
ObitIOCode 
ObitTableIDI_ANTENNAWriteRow  (ObitTableIDI_ANTENNA *in, olong iIDI_ANTENNARow, ObitTableIDI_ANTENNARow *row,
		      ObitErr *err)
{
  ObitIOCode retCode = OBIT_IO_SpecErr;
  gshort    *siRow;
  odouble   *dRow;
  oint      *iRow, i;
  ofloat    *fRow;
  gchar     *cRow;
  gboolean  *lRow;
  guint8    *bRow;
  gchar *routine = "ObitTableIDI_ANTENNAWriteRow";
  

  /* error checks */
  g_assert (ObitErrIsA(err));
  if (err->error) return retCode;
  g_assert (ObitIsA(in, &myClassInfo));

  if (in->myStatus == OBIT_Inactive) {
    Obit_log_error(err, OBIT_Error,
		   "IDI_ANTENNA Table is inactive for %s ", in->name);
    return retCode;
 }

  /* Typed pointers to row of data */  
  dRow  = (odouble*)in->buffer;
  siRow = (gshort*)in->buffer;
  iRow  = (oint*)in->buffer;
  fRow  = (ofloat*)in->buffer;
  cRow  = (gchar*)in->buffer;
  lRow  = (gboolean*)in->buffer;
  bRow  = (guint8*)in->buffer;
  
  /* Make full copy of all data */
  dRow[in->timeOff] = row->time;
  fRow[in->time_intervalOff] = row->time_interval;
  iRow[in->antenna_noOff] = row->antenna_no;
  iRow[in->arrayOff] = row->array;
  iRow[in->freqidOff] = row->freqid;
  iRow[in->no_levelsOff] = row->no_levels;
  fRow[in->PolAngAOff] = row->PolAngA;
  fRow[in->PolAngBOff] = row->PolAngB;
  if (in->AntNameCol >= 0) { 
    for (i=0; i<in->myDesc->repeat[in->AntNameCol]; i++) 
      cRow[in->AntNameOff+i] = row->AntName[i];
  } 
  if (in->polTypeACol >= 0) { 
    for (i=0; i<in->myDesc->repeat[in->polTypeACol]; i++) 
      cRow[in->polTypeAOff+i] = row->polTypeA[i];
  } 
  if (in->PolCalACol >= 0) { 
    for (i=0; i<in->myDesc->repeat[in->PolCalACol]; i++) 
      fRow[in->PolCalAOff+i] = row->PolCalA[i];
  } 
  if (in->polTypeBCol >= 0) { 
    for (i=0; i<in->myDesc->repeat[in->polTypeBCol]; i++) 
      cRow[in->polTypeBOff+i] = row->polTypeB[i];
  } 
  if (in->PolCalBCol >= 0) { 
    for (i=0; i<in->myDesc->repeat[in->PolCalBCol]; i++) 
      fRow[in->PolCalBOff+i] = row->PolCalB[i];
  } 

  /* copy status */
  iRow[in->myDesc->statusOff] = row->status;
   
  /* Write one row */
  in->myDesc->numRowBuff = 1;
 
  /* Write row iIDI_ANTENNARow */
  retCode = ObitTableWrite ((ObitTable*)in, iIDI_ANTENNARow, NULL,  err);
  if (err->error) 
    Obit_traceback_val (err, routine,in->name, retCode);

  return retCode;
} /*  end ObitTableIDI_ANTENNAWriteRow */

/**
 * Shutdown I/O.
 * \param in Pointer to object to be closed.
 * \param err ObitErr for reporting errors.
 * \return error code, OBIT_IO_OK=> OK
 */
ObitIOCode ObitTableIDI_ANTENNAClose (ObitTableIDI_ANTENNA *in, ObitErr *err)
{
  ObitIOCode retCode = OBIT_IO_SpecErr;
  gchar *routine = "ObitTableIDI_ANTENNAClose";

  /* error checks */
  g_assert (ObitErrIsA(err));
  if (err->error) return retCode;
  g_assert (ObitIsA((Obit*)in, &myClassInfo));
  /* Something going on? */
  if (in->myStatus == OBIT_Inactive) return OBIT_IO_OK;

  /* Update keywords on descriptor if not ReadOnly*/
  if (in->myDesc->access != OBIT_IO_ReadOnly) 
    ObitTableIDI_ANTENNADumpKey (in, err);
  if (err->error) 
    Obit_traceback_val (err, routine, in->name, retCode);

  /* Close */
  retCode = ObitTableClose ((ObitTable*)in, err);
  if (err->error) 
    Obit_traceback_val (err, routine, in->name, retCode);

  return retCode;
} /* end ObitTableIDI_ANTENNAClose */

