/**
 * Get table specific information from the infolist or descriptor
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableIDI_ANTENNAUpdate (ObitTableIDI_ANTENNA *in, ObitErr *err)
{
  olong i;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM];
  ObitTableDesc *desc;
   union ObitInfoListEquiv InfoReal; 

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Get Keywords */
   /* tabrev */
  in->tabrev = 1; 
  ObitInfoListGetTest(in->myDesc->info, "TABREV", &type, dim, 
		       (gpointer)&in->tabrev);
   /* no_stkd */
  in->no_stkd = 1; 
  ObitInfoListGetTest(in->myDesc->info, "NO_STKD", &type, dim, 
		       (gpointer)&in->no_stkd);
   /* stk_1 */
  in->stk_1 = -1; 
  ObitInfoListGetTest(in->myDesc->info, "STK_1", &type, dim, 
		       (gpointer)&in->stk_1);
   /* no_band */
  in->no_band = 1; 
  ObitInfoListGetTest(in->myDesc->info, "NO_BAND", &type, dim, 
		       (gpointer)&in->no_band);
   /* no_chan */
  in->no_chan = 1; 
  ObitInfoListGetTest(in->myDesc->info, "NO_CHAN", &type, dim, 
		       (gpointer)&in->no_chan);
  in->ref_freq = 1.0; 
  InfoReal.dbl = 1.0; 
  type = OBIT_double;
  ObitInfoListGetTest(in->myDesc->info, "REF_FREQ", &type, dim, 
		       (gpointer)&InfoReal);
  if (type==OBIT_double) in->ref_freq = InfoReal.dbl;
  if (type==OBIT_float)  in->ref_freq = InfoReal.flt;
  in->chan_bw = 1.0; 
  InfoReal.dbl = 1.0; 
  type = OBIT_double;
  ObitInfoListGetTest(in->myDesc->info, "CHAN_BW", &type, dim, 
		       (gpointer)&InfoReal);
  if (type==OBIT_double) in->chan_bw = InfoReal.dbl;
  if (type==OBIT_float)  in->chan_bw = InfoReal.flt;
   /* ref_pixl */
  in->ref_pixl = 1.0; 
  ObitInfoListGetTest(in->myDesc->info, "REF_PIXL", &type, dim, 
		       (gpointer)&in->ref_pixl);
   /* obscode */
  strncpy (in->obscode, "    ", MAXKEYCHARTABLEIDI_ANTENNA); 
  ObitInfoListGetTest(in->myDesc->info, "OBSCODE", &type, dim, 
		       (gpointer)&in->obscode);
   /* ArrName */
  if (!ObitInfoListGet(in->myDesc->info, "ARRNAM", &type, dim, 
		       (gpointer)&in->ArrName, err)) return;
   /* RefDate */
  strncpy (in->RefDate, "YYYY-MM-DD", MAXKEYCHARTABLEIDI_ANTENNA); 
  ObitInfoListGetTest(in->myDesc->info, "RDATE", &type, dim, 
		       (gpointer)&in->RefDate);
   /* numPCal */
  in->numPCal = 4; 
  ObitInfoListGetTest(in->myDesc->info, "NOPCAL", &type, dim, 
		       (gpointer)&in->numPCal);
   /* polType */
  strncpy (in->polType, "        ", MAXKEYCHARTABLEIDI_ANTENNA); 
  ObitInfoListGetTest(in->myDesc->info, "POLTYPE", &type, dim, 
		       (gpointer)&in->polType);

  /* initialize column numbers/offsets */
  in->timeOff = -1;
  in->timeCol = -1;
  in->time_intervalOff = -1;
  in->time_intervalCol = -1;
  in->AntNameOff = -1;
  in->AntNameCol = -1;
  in->antenna_noOff = -1;
  in->antenna_noCol = -1;
  in->arrayOff = -1;
  in->arrayCol = -1;
  in->freqidOff = -1;
  in->freqidCol = -1;
  in->no_levelsOff = -1;
  in->no_levelsCol = -1;
  in->polTypeAOff = -1;
  in->polTypeACol = -1;
  in->PolAngAOff = -1;
  in->PolAngACol = -1;
  in->PolCalAOff = -1;
  in->PolCalACol = -1;
  in->polTypeBOff = -1;
  in->polTypeBCol = -1;
  in->PolAngBOff = -1;
  in->PolAngBCol = -1;
  in->PolCalBOff = -1;
  in->PolCalBCol = -1;
  /* Find columns and set offsets */
  desc = in->myDesc;
  if (desc->FieldName) {
    for (i=0; i<desc->nfield; i++) {
      if (!strncmp (desc->FieldName[i], "TIME    ", 8)) {
	 in->timeOff = desc->offset[i];
 	 in->timeCol = i;
      }
      if (!strncmp (desc->FieldName[i], "TIME_INTERVAL", 13)) {
	 in->time_intervalOff = desc->offset[i];
 	 in->time_intervalCol = i;
      }
      if (!strncmp (desc->FieldName[i], "ANNAME  ", 8)) {
	 in->AntNameOff = desc->offset[i];
 	 in->AntNameCol = i;
      }
      if (!strncmp (desc->FieldName[i], "ANTENNA_NO", 10)) {
	 in->antenna_noOff = desc->offset[i];
 	 in->antenna_noCol = i;
      }
      if (!strncmp (desc->FieldName[i], "ARRAY", 5)) {
	 in->arrayOff = desc->offset[i];
 	 in->arrayCol = i;
      }
      if (!strncmp (desc->FieldName[i], "FREQID", 6)) {
	 in->freqidOff = desc->offset[i];
 	 in->freqidCol = i;
      }
      if (!strncmp (desc->FieldName[i], "NO_LEVELS", 9)) {
	 in->no_levelsOff = desc->offset[i];
 	 in->no_levelsCol = i;
      }
      if (!strncmp (desc->FieldName[i], "POLTYA  ", 8)) {
	 in->polTypeAOff = desc->offset[i];
 	 in->polTypeACol = i;
      }
      if (!strncmp (desc->FieldName[i], "POLAA   ", 8)) {
	 in->PolAngAOff = desc->offset[i];
 	 in->PolAngACol = i;
      }
      if (!strncmp (desc->FieldName[i], "POLCALA", 7)) {
	 in->PolCalAOff = desc->offset[i];
 	 in->PolCalACol = i;
      }
      if (!strncmp (desc->FieldName[i], "POLTYB ", 7)) {
	 in->polTypeBOff = desc->offset[i];
 	 in->polTypeBCol = i;
      }
      if (!strncmp (desc->FieldName[i], "POLAB  ", 7)) {
	 in->PolAngBOff = desc->offset[i];
 	 in->PolAngBCol = i;
      }
      if (!strncmp (desc->FieldName[i], "POLCALB", 7)) {
	 in->PolCalBOff = desc->offset[i];
 	 in->PolCalBCol = i;
      }
     }
  }

  /* Check required columns */
  Obit_return_if_fail((in->timeOff > -1), err,
       "ObitTableIDI_ANTENNAUpdate: Could not find column time");
  Obit_return_if_fail((in->time_intervalOff > -1), err,
       "ObitTableIDI_ANTENNAUpdate: Could not find column time_interval");
  Obit_return_if_fail((in->AntNameOff > -1), err,
       "ObitTableIDI_ANTENNAUpdate: Could not find column AntName");
  Obit_return_if_fail((in->antenna_noOff > -1), err,
       "ObitTableIDI_ANTENNAUpdate: Could not find column antenna_no");
  Obit_return_if_fail((in->arrayOff > -1), err,
       "ObitTableIDI_ANTENNAUpdate: Could not find column array");
  Obit_return_if_fail((in->freqidOff > -1), err,
       "ObitTableIDI_ANTENNAUpdate: Could not find column freqid");
  Obit_return_if_fail((in->no_levelsOff > -1), err,
       "ObitTableIDI_ANTENNAUpdate: Could not find column no_levels");
  Obit_return_if_fail((in->polTypeAOff > -1), err,
       "ObitTableIDI_ANTENNAUpdate: Could not find column polTypeA");
  Obit_return_if_fail((in->PolAngAOff > -1), err,
       "ObitTableIDI_ANTENNAUpdate: Could not find column PolAngA");
  Obit_return_if_fail((in->PolCalAOff > -1), err,
       "ObitTableIDI_ANTENNAUpdate: Could not find column PolCalA");
  Obit_return_if_fail((in->polTypeBOff > -1), err,
       "ObitTableIDI_ANTENNAUpdate: Could not find column polTypeB");
  Obit_return_if_fail((in->PolAngBOff > -1), err,
       "ObitTableIDI_ANTENNAUpdate: Could not find column PolAngB");
} /* end ObitTableIDI_ANTENNAUpdate */

/**
 * Copy table specific (keyword) information  to infolist.
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableIDI_ANTENNADumpKey (ObitTableIDI_ANTENNA *in, ObitErr *err)
{
  ObitInfoList *info=NULL;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM] = {1,1,1,1,1};

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Set Keywords */
  if (in->myIO!=NULL) info = ((ObitTableDesc*)(in->myIO->myDesc))->info;
  else info = in->myDesc->info;
  /* tabrev */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "TABREV", type, dim, 
		  (gpointer)&in->tabrev);
  /* no_stkd */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_STKD", type, dim, 
		  (gpointer)&in->no_stkd);
  /* stk_1 */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "STK_1", type, dim, 
		  (gpointer)&in->stk_1);
  /* no_band */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_BAND", type, dim, 
		  (gpointer)&in->no_band);
  /* no_chan */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_CHAN", type, dim, 
		  (gpointer)&in->no_chan);
  /* ref_freq */
  type  = OBIT_double;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "REF_FREQ", type, dim, 
		  (gpointer)&in->ref_freq);
  /* chan_bw */
  type  = OBIT_double;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "CHAN_BW", type, dim, 
		  (gpointer)&in->chan_bw);
  /* ref_pixl */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "REF_PIXL", type, dim, 
		  (gpointer)&in->ref_pixl);
  /* obscode */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEIDI_ANTENNA;
  ObitInfoListAlwaysPut(info, "OBSCODE", type, dim, 
		  (gpointer)&in->obscode);
  /* ArrName */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEIDI_ANTENNA;
  ObitInfoListAlwaysPut(info, "ARRNAM", type, dim, 
		  (gpointer)&in->ArrName);
  /* RefDate */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEIDI_ANTENNA;
  ObitInfoListAlwaysPut(info, "RDATE", type, dim, 
		  (gpointer)&in->RefDate);
  /* numPCal */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NOPCAL", type, dim, 
		  (gpointer)&in->numPCal);
  /* polType */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEIDI_ANTENNA;
  ObitInfoListAlwaysPut(info, "POLTYPE", type, dim, 
		  (gpointer)&in->polType);
   
} /* end ObitTableIDI_ANTENNADumpKey */
