/**
 * Get table specific information from the infolist or descriptor
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableIDI_ARRAY_GEOMETRYUpdate (ObitTableIDI_ARRAY_GEOMETRY *in, ObitErr *err)
{
  olong i;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM];
  ObitTableDesc *desc;
   union ObitInfoListEquiv InfoReal; 

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Get Keywords */
   /* tabrev */
  in->tabrev = 1; 
  ObitInfoListGetTest(in->myDesc->info, "TABREV", &type, dim, 
		       (gpointer)&in->tabrev);
   /* no_stkd */
  in->no_stkd = 1; 
  ObitInfoListGetTest(in->myDesc->info, "NO_STKD", &type, dim, 
		       (gpointer)&in->no_stkd);
   /* stk_1 */
  in->stk_1 = -1; 
  ObitInfoListGetTest(in->myDesc->info, "STK_1", &type, dim, 
		       (gpointer)&in->stk_1);
   /* no_band */
  in->no_band = 1; 
  ObitInfoListGetTest(in->myDesc->info, "NO_BAND", &type, dim, 
		       (gpointer)&in->no_band);
   /* no_chan */
  in->no_chan = 1; 
  ObitInfoListGetTest(in->myDesc->info, "NO_CHAN", &type, dim, 
		       (gpointer)&in->no_chan);
  in->ref_freq = 1.0; 
  InfoReal.dbl = 1.0; 
  type = OBIT_double;
  ObitInfoListGetTest(in->myDesc->info, "REF_FREQ", &type, dim, 
		       (gpointer)&InfoReal);
  if (type==OBIT_double) in->ref_freq = InfoReal.dbl;
  if (type==OBIT_float)  in->ref_freq = InfoReal.flt;
  in->chan_bw = 1.0; 
  InfoReal.dbl = 1.0; 
  type = OBIT_double;
  ObitInfoListGetTest(in->myDesc->info, "CHAN_BW", &type, dim, 
		       (gpointer)&InfoReal);
  if (type==OBIT_double) in->chan_bw = InfoReal.dbl;
  if (type==OBIT_float)  in->chan_bw = InfoReal.flt;
   /* ref_pixl */
  in->ref_pixl = 1.0; 
  ObitInfoListGetTest(in->myDesc->info, "REF_PIXL", &type, dim, 
		       (gpointer)&in->ref_pixl);
   /* obscode */
  strncpy (in->obscode, "    ", MAXKEYCHARTABLEIDI_ARRAY_GEOMETRY); 
  ObitInfoListGetTest(in->myDesc->info, "OBSCODE", &type, dim, 
		       (gpointer)&in->obscode);
   /* ArrName */
  if (!ObitInfoListGet(in->myDesc->info, "ARRNAM", &type, dim, 
		       (gpointer)&in->ArrName, err)) return;
   /* RefDate */
  strncpy (in->RefDate, "YYYY-MM-DD", MAXKEYCHARTABLEIDI_ARRAY_GEOMETRY); 
  ObitInfoListGetTest(in->myDesc->info, "RDATE", &type, dim, 
		       (gpointer)&in->RefDate);
   /* frame */
  strncpy (in->frame, "GEOCENTRIC", MAXKEYCHARTABLEIDI_ARRAY_GEOMETRY); 
  ObitInfoListGetTest(in->myDesc->info, "FRAME", &type, dim, 
		       (gpointer)&in->frame);
   /* numOrb */
  in->numOrb = 0; 
  ObitInfoListGetTest(in->myDesc->info, "NUMORB", &type, dim, 
		       (gpointer)&in->numOrb);
  in->Freq = 1.0; 
  InfoReal.dbl = 1.0; 
  type = OBIT_double;
  ObitInfoListGetTest(in->myDesc->info, "FREQ", &type, dim, 
		       (gpointer)&InfoReal);
  if (type==OBIT_double) in->Freq = InfoReal.dbl;
  if (type==OBIT_float)  in->Freq = InfoReal.flt;
   /* TimeSys */
  strncpy (in->TimeSys, "IAT", MAXKEYCHARTABLEIDI_ARRAY_GEOMETRY); 
  ObitInfoListGetTest(in->myDesc->info, "TIMSYS", &type, dim, 
		       (gpointer)&in->TimeSys);
  in->GSTiat0 = 0.0; 
  InfoReal.dbl = 0.0; 
  type = OBIT_double;
  ObitInfoListGetTest(in->myDesc->info, "GSTIA0", &type, dim, 
		       (gpointer)&InfoReal);
  if (type==OBIT_double) in->GSTiat0 = InfoReal.dbl;
  if (type==OBIT_float)  in->GSTiat0 = InfoReal.flt;
  in->DegDay = 360.0; 
  InfoReal.dbl = 360.0; 
  type = OBIT_double;
  ObitInfoListGetTest(in->myDesc->info, "DEGPDY", &type, dim, 
		       (gpointer)&InfoReal);
  if (type==OBIT_double) in->DegDay = InfoReal.dbl;
  if (type==OBIT_float)  in->DegDay = InfoReal.flt;
   /* ut1Utc */
  if (!ObitInfoListGet(in->myDesc->info, "UT1UTC", &type, dim, 
		       (gpointer)&InfoReal, err)) return;
  if (type==OBIT_double) in->ut1Utc = InfoReal.dbl;
  if (type==OBIT_float)  in->ut1Utc = InfoReal.flt;
  in->iatUtc = 0.0; 
  InfoReal.dbl = 0.0; 
  type = OBIT_double;
  ObitInfoListGetTest(in->myDesc->info, "IATUTC", &type, dim, 
		       (gpointer)&InfoReal);
  if (type==OBIT_double) in->iatUtc = InfoReal.dbl;
  if (type==OBIT_float)  in->iatUtc = InfoReal.flt;
  in->PolarX = 0.0; 
  InfoReal.dbl = 0.0; 
  type = OBIT_double;
  ObitInfoListGetTest(in->myDesc->info, "POLARX", &type, dim, 
		       (gpointer)&InfoReal);
  if (type==OBIT_double) in->PolarX = InfoReal.dbl;
  if (type==OBIT_float)  in->PolarX = InfoReal.flt;
  in->PolarY = 0.0; 
  InfoReal.dbl = 0.0; 
  type = OBIT_double;
  ObitInfoListGetTest(in->myDesc->info, "POLARY", &type, dim, 
		       (gpointer)&InfoReal);
  if (type==OBIT_double) in->PolarY = InfoReal.dbl;
  if (type==OBIT_float)  in->PolarY = InfoReal.flt;
  in->ArrayX = 0.0; 
  InfoReal.dbl = 0.0; 
  type = OBIT_double;
  ObitInfoListGetTest(in->myDesc->info, "ARRAYX", &type, dim, 
		       (gpointer)&InfoReal);
  if (type==OBIT_double) in->ArrayX = InfoReal.dbl;
  if (type==OBIT_float)  in->ArrayX = InfoReal.flt;
  in->ArrayY = 0.0; 
  InfoReal.dbl = 0.0; 
  type = OBIT_double;
  ObitInfoListGetTest(in->myDesc->info, "ARRAYY", &type, dim, 
		       (gpointer)&InfoReal);
  if (type==OBIT_double) in->ArrayY = InfoReal.dbl;
  if (type==OBIT_float)  in->ArrayY = InfoReal.flt;
  in->ArrayZ = 0.0; 
  InfoReal.dbl = 0.0; 
  type = OBIT_double;
  ObitInfoListGetTest(in->myDesc->info, "ARRAYZ", &type, dim, 
		       (gpointer)&InfoReal);
  if (type==OBIT_double) in->ArrayZ = InfoReal.dbl;
  if (type==OBIT_float)  in->ArrayZ = InfoReal.flt;

  /* initialize column numbers/offsets */
  in->AntNameOff = -1;
  in->AntNameCol = -1;
  in->StaXYZOff = -1;
  in->StaXYZCol = -1;
  in->derXYZOff = -1;
  in->derXYZCol = -1;
  in->OrbParmOff = -1;
  in->OrbParmCol = -1;
  in->noStaOff = -1;
  in->noStaCol = -1;
  in->mntStaOff = -1;
  in->mntStaCol = -1;
  in->staXofOff = -1;
  in->staXofCol = -1;
  /* Find columns and set offsets */
  desc = in->myDesc;
  if (desc->FieldName) {
    for (i=0; i<desc->nfield; i++) {
      if (!strncmp (desc->FieldName[i], "ANNAME  ", 8)) {
	 in->AntNameOff = desc->offset[i];
 	 in->AntNameCol = i;
      }
      if (!strncmp (desc->FieldName[i], "STABXYZ ", 8)) {
	 in->StaXYZOff = desc->offset[i];
 	 in->StaXYZCol = i;
      }
      if (!strncmp (desc->FieldName[i], "DERXYZ  ", 8)) {
	 in->derXYZOff = desc->offset[i];
 	 in->derXYZCol = i;
      }
      if (!strncmp (desc->FieldName[i], "ORBPARM ", 8)) {
	 in->OrbParmOff = desc->offset[i];
 	 in->OrbParmCol = i;
      }
      if (!strncmp (desc->FieldName[i], "NOSTA   ", 8)) {
	 in->noStaOff = desc->offset[i];
 	 in->noStaCol = i;
      }
      if (!strncmp (desc->FieldName[i], "MNTSTA  ", 8)) {
	 in->mntStaOff = desc->offset[i];
 	 in->mntStaCol = i;
      }
      if (!strncmp (desc->FieldName[i], "STAXOF  ", 8)) {
	 in->staXofOff = desc->offset[i];
 	 in->staXofCol = i;
      }
     }
  }

  /* Check required columns */
  Obit_return_if_fail((in->AntNameOff > -1), err,
       "ObitTableIDI_ARRAY_GEOMETRYUpdate: Could not find column AntName");
  Obit_return_if_fail((in->StaXYZOff > -1), err,
       "ObitTableIDI_ARRAY_GEOMETRYUpdate: Could not find column StaXYZ");
  Obit_return_if_fail((in->derXYZOff > -1), err,
       "ObitTableIDI_ARRAY_GEOMETRYUpdate: Could not find column derXYZ");
  Obit_return_if_fail((in->OrbParmOff > -1), err,
       "ObitTableIDI_ARRAY_GEOMETRYUpdate: Could not find column OrbParm");
  Obit_return_if_fail((in->noStaOff > -1), err,
       "ObitTableIDI_ARRAY_GEOMETRYUpdate: Could not find column noSta");
  Obit_return_if_fail((in->mntStaOff > -1), err,
       "ObitTableIDI_ARRAY_GEOMETRYUpdate: Could not find column mntSta");
} /* end ObitTableIDI_ARRAY_GEOMETRYUpdate */

