{
  ObitIOCode retCode = OBIT_IO_SpecErr;
  gint32 dim[MAXINFOELEMDIM] = {1,1,1,1,1};
  olong nRowPIO;
  gchar *routine = "ObitTableIDI_BANDPASSOpen";

  /* Class initialization if needed */
  if (!myClassInfo.initialized) ObitTableIDI_BANDPASSClassInit();

  /* error checks */
  g_assert (ObitErrIsA(err));
  if (err->error) return retCode;
  g_assert (ObitIsA(in, &myClassInfo));

   /* Do one row at a time */
   nRowPIO = 1;
   ObitInfoListPut(in->info, "nRowPIO", OBIT_long, dim, (gconstpointer)&nRowPIO, err);
   if (err->error) /* add traceback,return */
     Obit_traceback_val (err, routine, in->name, retCode);
   
   /* use parent class open */
   retCode = ObitTableOpen ((ObitTable*)in, access, err);
   if ((retCode!=OBIT_IO_OK) || (err->error)) /* add traceback,return */
     Obit_traceback_val (err, routine, in->name, retCode);
   
   /* Update class specific info */
   ObitTableIDI_BANDPASSUpdate (in, err);
   
   return retCode;
} /* end ObitTableIDI_BANDPASSOpen */

/**
 * Read a table row and return an easily digested version.
 * Scalar values are copied but for array values, pointers 
 * into the data array are returned.
 * \param in       Table to read
 * \param iIDI_BANDPASSRow   Row number, -1 -> next
 * \param row      Table Row structure to receive data
 * \param err ObitErr for reporting errors.
 * \return return code, OBIT_IO_OK=> OK
 */
ObitIOCode 
ObitTableIDI_BANDPASSReadRow  (ObitTableIDI_BANDPASS *in, olong iIDI_BANDPASSRow, ObitTableIDI_BANDPASSRow *row,
		     ObitErr *err)
{
  ObitIOCode retCode = OBIT_IO_SpecErr;
  odouble   *dRow;
  oint      *iRow;
  gshort    *siRow;
  ofloat    *fRow;
  gchar     *cRow;
  gboolean  *lRow;
  guint8    *bRow;
  gchar *routine = "ObitTableIDI_BANDPASSReadRow";
  
  /* error checks */
  g_assert (ObitErrIsA(err));
  if (err->error) return retCode;
  g_assert (ObitIsA(in, &myClassInfo));

  if (in->myStatus == OBIT_Inactive) {
    Obit_log_error(err, OBIT_Error,
		   "IDI_BANDPASS Table is inactive for  %s ", in->name);
    return retCode;
 }

  /* read row iIDI_BANDPASSRow */
  retCode = ObitTableRead ((ObitTable*)in, iIDI_BANDPASSRow, NULL,  err);
  if (err->error) 
    Obit_traceback_val (err, routine, in->name, retCode);

  /* Typed pointers to row of data */  
  dRow  = (odouble*)in->buffer;
  iRow  = (oint*)in->buffer;
  siRow = (gshort*)in->buffer;
  fRow  = (ofloat*)in->buffer;
  cRow  = (gchar*)in->buffer;
  lRow  = (gboolean*)in->buffer;
  bRow  = (guint8*)in->buffer;
  
  /* Copy scalar fields, for arrays only set pointer*/
  row->Time = dRow[in->TimeOff];
  row->TimeI = fRow[in->TimeIOff];
  row->SourID = iRow[in->SourIDOff];
  row->antNo = iRow[in->antNoOff];
  row->Array = iRow[in->ArrayOff];
  row->fqid = iRow[in->fqidOff];
  row->bandwidth = fRow + in->bandwidthOff;
  row->band_freq = dRow + in->band_freqOff;
  if (in->numPol>=1) row->refant1 = iRow + in->refant1Off;
  if (in->numPol>=1) row->breal1 = fRow + in->breal1Off;
  if (in->numPol>=1) row->bimag1 = fRow + in->bimag1Off;
  if (in->numPol>=2) row->refant2 = iRow + in->refant2Off;
  if (in->numPol>=2) row->breal2 = fRow + in->breal2Off;
  if (in->numPol>=2) row->bimag2 = fRow + in->bimag2Off;
  row->status = iRow[in->myDesc->statusOff];

  return retCode;
} /*  end ObitTableIDI_BANDPASSReadRow */

/**
 * Attach an ObitTableRow to the buffer of an ObitTable.
 * This is only useful prior to filling a row structure in preparation .
 * for a WriteRow operation.  Array members of the Row structure are .
 * pointers to independently allocated memory, this routine allows using .
 * the table IO buffer instead of allocating yet more memory..
 * This routine need only be called once to initialize a Row structure for write..
 * \param in  Table with buffer to be written 
 * \param row Table Row structure to attach 
 * \param err ObitErr for reporting errors.
 */
void 
ObitTableIDI_BANDPASSSetRow  (ObitTableIDI_BANDPASS *in, ObitTableIDI_BANDPASSRow *row,
		     ObitErr *err)
{
  odouble   *dRow;
  oint      *iRow;
  gshort    *siRow;
  ofloat    *fRow;
  gchar     *cRow;
  gboolean  *lRow;
  guint8    *bRow;
  
  /* error checks */
  g_assert (ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));
  g_assert (ObitIsA(row, &myRowClassInfo));

  if (in->myStatus == OBIT_Inactive) {
    Obit_log_error(err, OBIT_Error,
		   "IDI_BANDPASS Table is inactive for  %s ", in->name);
    return;
 }

  /* Typed pointers to row of data */  
  dRow  = (odouble*)in->buffer;
  iRow  = (oint*)in->buffer;
  siRow = (gshort*)in->buffer;
  fRow  = (ofloat*)in->buffer;
  cRow  = (gchar*)in->buffer;
  lRow  = (gboolean*)in->buffer;
  bRow  = (guint8*)in->buffer;
  
  /* Set row pointers to buffer */
  row->bandwidth = fRow + in->bandwidthOff;
  row->band_freq = dRow + in->band_freqOff;
  if (in->numPol>=1) row->refant1 = iRow + in->refant1Off;
  if (in->numPol>=1) row->breal1 = fRow + in->breal1Off;
  if (in->numPol>=1) row->bimag1 = fRow + in->bimag1Off;
  if (in->numPol>=2) row->refant2 = iRow + in->refant2Off;
  if (in->numPol>=2) row->breal2 = fRow + in->breal2Off;
  if (in->numPol>=2) row->bimag2 = fRow + in->bimag2Off;

} /*  end ObitTableIDI_BANDPASSSetRow */

/**
 * Write a table row.
 * Before calling this routine, the row structure needs to be initialized
 * and filled with data. The array members of the row structure are  
 * pointers to independently allocated memory.  These pointers can be set to the 
 * correct table buffer locations using ObitTableIDI_BANDPASSSetRow  
 * \param in       Table to read
 * \param iIDI_BANDPASSRow   Row number, -1 -> next
 * \param row Table Row structure containing data
 * \param err ObitErr for reporting errors.
 * \return return code, OBIT_IO_OK=> OK
 */
ObitIOCode 
ObitTableIDI_BANDPASSWriteRow  (ObitTableIDI_BANDPASS *in, olong iIDI_BANDPASSRow, ObitTableIDI_BANDPASSRow *row,
		      ObitErr *err)
{
  ObitIOCode retCode = OBIT_IO_SpecErr;
  gshort    *siRow;
  odouble   *dRow;
  oint      *iRow, i;
  ofloat    *fRow;
  gchar     *cRow;
  gboolean  *lRow;
  guint8    *bRow;
  gchar *routine = "ObitTableIDI_BANDPASSWriteRow";
  

  /* error checks */
  g_assert (ObitErrIsA(err));
  if (err->error) return retCode;
  g_assert (ObitIsA(in, &myClassInfo));

  if (in->myStatus == OBIT_Inactive) {
    Obit_log_error(err, OBIT_Error,
		   "IDI_BANDPASS Table is inactive for %s ", in->name);
    return retCode;
 }

  /* Typed pointers to row of data */  
  dRow  = (odouble*)in->buffer;
  siRow = (gshort*)in->buffer;
  iRow  = (oint*)in->buffer;
  fRow  = (ofloat*)in->buffer;
  cRow  = (gchar*)in->buffer;
  lRow  = (gboolean*)in->buffer;
  bRow  = (guint8*)in->buffer;
  
  /* Make full copy of all data */
  dRow[in->TimeOff] = row->Time;
  fRow[in->TimeIOff] = row->TimeI;
  iRow[in->SourIDOff] = row->SourID;
  iRow[in->antNoOff] = row->antNo;
  iRow[in->ArrayOff] = row->Array;
  iRow[in->fqidOff] = row->fqid;
  if (in->bandwidthCol >= 0) { 
    for (i=0; i<in->myDesc->repeat[in->bandwidthCol]; i++) 
      fRow[in->bandwidthOff+i] = row->bandwidth[i];
  } 
  if (in->band_freqCol >= 0) { 
    for (i=0; i<in->myDesc->repeat[in->band_freqCol]; i++) 
      dRow[in->band_freqOff+i] = row->band_freq[i];
  } 
  if (in->numPol>=1)
    if (in->refant1Col >= 0) { 
      for (i=0; i<in->myDesc->repeat[in->refant1Col]; i++) 
        iRow[in->refant1Off+i] = row->refant1[i];
    } 
  if (in->numPol>=1)
    if (in->breal1Col >= 0) { 
      for (i=0; i<in->myDesc->repeat[in->breal1Col]; i++) 
        fRow[in->breal1Off+i] = row->breal1[i];
    } 
  if (in->numPol>=1)
    if (in->bimag1Col >= 0) { 
      for (i=0; i<in->myDesc->repeat[in->bimag1Col]; i++) 
        fRow[in->bimag1Off+i] = row->bimag1[i];
    } 
  if (in->numPol>=2)
    if (in->refant2Col >= 0) { 
      for (i=0; i<in->myDesc->repeat[in->refant2Col]; i++) 
        iRow[in->refant2Off+i] = row->refant2[i];
    } 
  if (in->numPol>=2)
    if (in->breal2Col >= 0) { 
      for (i=0; i<in->myDesc->repeat[in->breal2Col]; i++) 
        fRow[in->breal2Off+i] = row->breal2[i];
    } 
  if (in->numPol>=2)
    if (in->bimag2Col >= 0) { 
      for (i=0; i<in->myDesc->repeat[in->bimag2Col]; i++) 
        fRow[in->bimag2Off+i] = row->bimag2[i];
    } 

  /* copy status */
  iRow[in->myDesc->statusOff] = row->status;
   
  /* Write one row */
  in->myDesc->numRowBuff = 1;
 
  /* Write row iIDI_BANDPASSRow */
  retCode = ObitTableWrite ((ObitTable*)in, iIDI_BANDPASSRow, NULL,  err);
  if (err->error) 
    Obit_traceback_val (err, routine,in->name, retCode);

  return retCode;
} /*  end ObitTableIDI_BANDPASSWriteRow */

/**
 * Shutdown I/O.
 * \param in Pointer to object to be closed.
 * \param err ObitErr for reporting errors.
 * \return error code, OBIT_IO_OK=> OK
 */
ObitIOCode ObitTableIDI_BANDPASSClose (ObitTableIDI_BANDPASS *in, ObitErr *err)
{
  ObitIOCode retCode = OBIT_IO_SpecErr;
  gchar *routine = "ObitTableIDI_BANDPASSClose";

  /* error checks */
  g_assert (ObitErrIsA(err));
  if (err->error) return retCode;
  g_assert (ObitIsA((Obit*)in, &myClassInfo));
  /* Something going on? */
  if (in->myStatus == OBIT_Inactive) return OBIT_IO_OK;

  /* Update keywords on descriptor if not ReadOnly*/
  if (in->myDesc->access != OBIT_IO_ReadOnly) 
    ObitTableIDI_BANDPASSDumpKey (in, err);
  if (err->error) 
    Obit_traceback_val (err, routine, in->name, retCode);

  /* Close */
  retCode = ObitTableClose ((ObitTable*)in, err);
  if (err->error) 
    Obit_traceback_val (err, routine, in->name, retCode);

  return retCode;
} /* end ObitTableIDI_BANDPASSClose */

