/**
 * Get table specific information from the infolist or descriptor
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableIDI_BANDPASSUpdate (ObitTableIDI_BANDPASS *in, ObitErr *err)
{
  olong i;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM];
  ObitTableDesc *desc;
   union ObitInfoListEquiv InfoReal; 

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Get Keywords */
   /* tabrev */
  in->tabrev = 1; 
  ObitInfoListGetTest(in->myDesc->info, "TABREV", &type, dim, 
		       (gpointer)&in->tabrev);
   /* numPol */
  if (!ObitInfoListGet(in->myDesc->info, "NO_POL", &type, dim, 
		       (gpointer)&in->numPol, err)) return;
   /* no_stkd */
  in->no_stkd = 1; 
  ObitInfoListGetTest(in->myDesc->info, "NO_STKD", &type, dim, 
		       (gpointer)&in->no_stkd);
   /* stk_1 */
  in->stk_1 = -1; 
  ObitInfoListGetTest(in->myDesc->info, "STK_1", &type, dim, 
		       (gpointer)&in->stk_1);
   /* no_band */
  in->no_band = 1; 
  ObitInfoListGetTest(in->myDesc->info, "NO_BAND", &type, dim, 
		       (gpointer)&in->no_band);
   /* no_chan */
  in->no_chan = 1; 
  ObitInfoListGetTest(in->myDesc->info, "NO_CHAN", &type, dim, 
		       (gpointer)&in->no_chan);
  in->ref_freq = 1.0; 
  InfoReal.dbl = 1.0; 
  type = OBIT_double;
  ObitInfoListGetTest(in->myDesc->info, "REF_FREQ", &type, dim, 
		       (gpointer)&InfoReal);
  if (type==OBIT_double) in->ref_freq = InfoReal.dbl;
  if (type==OBIT_float)  in->ref_freq = InfoReal.flt;
  in->chan_bw = 1.0; 
  InfoReal.dbl = 1.0; 
  type = OBIT_double;
  ObitInfoListGetTest(in->myDesc->info, "CHAN_BW", &type, dim, 
		       (gpointer)&InfoReal);
  if (type==OBIT_double) in->chan_bw = InfoReal.dbl;
  if (type==OBIT_float)  in->chan_bw = InfoReal.flt;
   /* ref_pixl */
  in->ref_pixl = 1.0; 
  ObitInfoListGetTest(in->myDesc->info, "REF_PIXL", &type, dim, 
		       (gpointer)&in->ref_pixl);
   /* obscode */
  strncpy (in->obscode, "    ", MAXKEYCHARTABLEIDI_BANDPASS); 
  ObitInfoListGetTest(in->myDesc->info, "OBSCODE", &type, dim, 
		       (gpointer)&in->obscode);
   /* ArrName */
  if (!ObitInfoListGet(in->myDesc->info, "ARRNAM", &type, dim, 
		       (gpointer)&in->ArrName, err)) return;
   /* RefDate */
  strncpy (in->RefDate, "YYYY-MM-DD", MAXKEYCHARTABLEIDI_BANDPASS); 
  ObitInfoListGetTest(in->myDesc->info, "RDATE", &type, dim, 
		       (gpointer)&in->RefDate);
   /* numAnt */
  if (!ObitInfoListGet(in->myDesc->info, "NO_ANT", &type, dim, 
		       (gpointer)&in->numAnt, err)) return;
   /* numBach */
  if (!ObitInfoListGet(in->myDesc->info, "NO_BACH", &type, dim, 
		       (gpointer)&in->numBach, err)) return;
   /* strtChn */
  if (!ObitInfoListGet(in->myDesc->info, "STRT_CHN", &type, dim, 
		       (gpointer)&in->strtChn, err)) return;

  /* initialize column numbers/offsets */
  in->TimeOff = -1;
  in->TimeCol = -1;
  in->TimeIOff = -1;
  in->TimeICol = -1;
  in->SourIDOff = -1;
  in->SourIDCol = -1;
  in->antNoOff = -1;
  in->antNoCol = -1;
  in->ArrayOff = -1;
  in->ArrayCol = -1;
  in->fqidOff = -1;
  in->fqidCol = -1;
  in->bandwidthOff = -1;
  in->bandwidthCol = -1;
  in->band_freqOff = -1;
  in->band_freqCol = -1;
  in->refant1Off = -1;
  in->refant1Col = -1;
  in->breal1Off = -1;
  in->breal1Col = -1;
  in->bimag1Off = -1;
  in->bimag1Col = -1;
  in->refant2Off = -1;
  in->refant2Col = -1;
  in->breal2Off = -1;
  in->breal2Col = -1;
  in->bimag2Off = -1;
  in->bimag2Col = -1;
  /* Find columns and set offsets */
  desc = in->myDesc;
  if (desc->FieldName) {
    for (i=0; i<desc->nfield; i++) {
      if (!strncmp (desc->FieldName[i], "TIME    ", 8)) {
	 in->TimeOff = desc->offset[i];
 	 in->TimeCol = i;
      }
      if (!strncmp (desc->FieldName[i], "TIME INTERVAL", 13)) {
	 in->TimeIOff = desc->offset[i];
 	 in->TimeICol = i;
      }
      if (!strncmp (desc->FieldName[i], "SOURCE_ID", 9)) {
	 in->SourIDOff = desc->offset[i];
 	 in->SourIDCol = i;
      }
      if (!strncmp (desc->FieldName[i], "ANTENNA_NO", 10)) {
	 in->antNoOff = desc->offset[i];
 	 in->antNoCol = i;
      }
      if (!strncmp (desc->FieldName[i], "ARRAY", 5)) {
	 in->ArrayOff = desc->offset[i];
 	 in->ArrayCol = i;
      }
      if (!strncmp (desc->FieldName[i], "FREQID  ", 8)) {
	 in->fqidOff = desc->offset[i];
 	 in->fqidCol = i;
      }
      if (!strncmp (desc->FieldName[i], "BANDWIDTH", 9)) {
	 in->bandwidthOff = desc->offset[i];
 	 in->bandwidthCol = i;
      }
      if (!strncmp (desc->FieldName[i], "BAND_FREQ", 9)) {
	 in->band_freqOff = desc->offset[i];
 	 in->band_freqCol = i;
      }
      if (!strncmp (desc->FieldName[i], "REFANT_1", 8)) {
	     in->refant1Off = desc->offset[i];
 	     in->refant1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "BREAL_1", 7)) {
	     in->breal1Off = desc->offset[i];
 	     in->breal1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "BIMAG_1", 7)) {
	     in->bimag1Off = desc->offset[i];
 	     in->bimag1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "REFANT_2", 8)) {
	     in->refant2Off = desc->offset[i];
 	     in->refant2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "BREAL_2", 7)) {
	     in->breal2Off = desc->offset[i];
 	     in->breal2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "BIMAG_2", 7)) {
	     in->bimag2Off = desc->offset[i];
 	     in->bimag2Col = i;
      }
     }
  }

  /* Check required columns */
  Obit_return_if_fail((in->TimeOff > -1), err,
       "ObitTableIDI_BANDPASSUpdate: Could not find column Time");
  Obit_return_if_fail((in->TimeIOff > -1), err,
       "ObitTableIDI_BANDPASSUpdate: Could not find column TimeI");
  Obit_return_if_fail((in->SourIDOff > -1), err,
       "ObitTableIDI_BANDPASSUpdate: Could not find column SourID");
  Obit_return_if_fail((in->antNoOff > -1), err,
       "ObitTableIDI_BANDPASSUpdate: Could not find column antNo");
  Obit_return_if_fail((in->ArrayOff > -1), err,
       "ObitTableIDI_BANDPASSUpdate: Could not find column Array");
  Obit_return_if_fail((in->fqidOff > -1), err,
       "ObitTableIDI_BANDPASSUpdate: Could not find column fqid");
  Obit_return_if_fail((in->bandwidthOff > -1), err,
       "ObitTableIDI_BANDPASSUpdate: Could not find column bandwidth");
  Obit_return_if_fail((in->band_freqOff > -1), err,
       "ObitTableIDI_BANDPASSUpdate: Could not find column band_freq");
  if (in->numPol>=1) {
    Obit_return_if_fail((in->refant1Off > -1), err,
       "ObitTableIDI_BANDPASSUpdate: Could not find column refant1");
    Obit_return_if_fail((in->breal1Off > -1), err,
       "ObitTableIDI_BANDPASSUpdate: Could not find column breal1");
  } /* end of if index in range */
  if (in->numPol>=2) {
    Obit_return_if_fail((in->refant2Off > -1), err,
       "ObitTableIDI_BANDPASSUpdate: Could not find column refant2");
    Obit_return_if_fail((in->breal2Off > -1), err,
       "ObitTableIDI_BANDPASSUpdate: Could not find column breal2");
  } /* end of if index in range */
} /* end ObitTableIDI_BANDPASSUpdate */

/**
 * Copy table specific (keyword) information  to infolist.
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableIDI_BANDPASSDumpKey (ObitTableIDI_BANDPASS *in, ObitErr *err)
{
  ObitInfoList *info=NULL;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM] = {1,1,1,1,1};

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Set Keywords */
  if (in->myIO!=NULL) info = ((ObitTableDesc*)(in->myIO->myDesc))->info;
  else info = in->myDesc->info;
  /* tabrev */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "TABREV", type, dim, 
		  (gpointer)&in->tabrev);
  /* numPol */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_POL", type, dim, 
		  (gpointer)&in->numPol);
  /* no_stkd */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_STKD", type, dim, 
		  (gpointer)&in->no_stkd);
  /* stk_1 */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "STK_1", type, dim, 
		  (gpointer)&in->stk_1);
  /* no_band */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_BAND", type, dim, 
		  (gpointer)&in->no_band);
  /* no_chan */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_CHAN", type, dim, 
		  (gpointer)&in->no_chan);
  /* ref_freq */
  type  = OBIT_double;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "REF_FREQ", type, dim, 
		  (gpointer)&in->ref_freq);
  /* chan_bw */
  type  = OBIT_double;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "CHAN_BW", type, dim, 
		  (gpointer)&in->chan_bw);
  /* ref_pixl */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "REF_PIXL", type, dim, 
		  (gpointer)&in->ref_pixl);
  /* obscode */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEIDI_BANDPASS;
  ObitInfoListAlwaysPut(info, "OBSCODE", type, dim, 
		  (gpointer)&in->obscode);
  /* ArrName */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEIDI_BANDPASS;
  ObitInfoListAlwaysPut(info, "ARRNAM", type, dim, 
		  (gpointer)&in->ArrName);
  /* RefDate */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEIDI_BANDPASS;
  ObitInfoListAlwaysPut(info, "RDATE", type, dim, 
		  (gpointer)&in->RefDate);
  /* numAnt */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_ANT", type, dim, 
		  (gpointer)&in->numAnt);
  /* numBach */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_BACH", type, dim, 
		  (gpointer)&in->numBach);
  /* strtChn */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "STRT_CHN", type, dim, 
		  (gpointer)&in->strtChn);
   
} /* end ObitTableIDI_BANDPASSDumpKey */
