/**
 * Get table specific information from the infolist or descriptor
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableIMUpdate (ObitTableIM *in, ObitErr *err)
{
  olong i;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM];
  ObitTableDesc *desc;
   union ObitInfoListEquiv InfoReal; 

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Get Keywords */
   /* RefDate */
  strncpy (in->RefDate, "YYYY-MM-DD", MAXKEYCHARTABLEIM); 
  ObitInfoListGetTest(in->myDesc->info, "RDATE", &type, dim, 
		       (gpointer)&in->RefDate);
   /* numPol */
  in->numPol = 1; 
  ObitInfoListGetTest(in->myDesc->info, "NO_POL", &type, dim, 
		       (gpointer)&in->numPol);
   /* obscode */
  strncpy (in->obscode, "AA000", MAXKEYCHARTABLEIM); 
  ObitInfoListGetTest(in->myDesc->info, "OBSCODE", &type, dim, 
		       (gpointer)&in->obscode);
   /* numStkd */
  if (!ObitInfoListGet(in->myDesc->info, "NO_STKD", &type, dim, 
		       (gpointer)&in->numStkd, err)) return;
   /* numBand */
  if (!ObitInfoListGet(in->myDesc->info, "NO_BAND", &type, dim, 
		       (gpointer)&in->numBand, err)) return;
   /* numChan */
  if (!ObitInfoListGet(in->myDesc->info, "NO_CHAN", &type, dim, 
		       (gpointer)&in->numChan, err)) return;
   /* refFreq */
  if (!ObitInfoListGet(in->myDesc->info, "REF_FREQ", &type, dim, 
		       (gpointer)&InfoReal, err)) return;
  if (type==OBIT_double) in->refFreq = InfoReal.dbl;
  if (type==OBIT_float)  in->refFreq = InfoReal.flt;
   /* chanBW */
  if (!ObitInfoListGet(in->myDesc->info, "CHAN_BW", &type, dim, 
		       (gpointer)&InfoReal, err)) return;
  if (type==OBIT_double) in->chanBW = InfoReal.dbl;
  if (type==OBIT_float)  in->chanBW = InfoReal.flt;
   /* refPixl */
  if (!ObitInfoListGet(in->myDesc->info, "REF_PIXL", &type, dim, 
		       (gpointer)&InfoReal, err)) return;
  if (type==OBIT_double) in->refPixl = InfoReal.dbl;
  if (type==OBIT_float)  in->refPixl = InfoReal.flt;
  in->stk1 = -1.0; 
  InfoReal.dbl = -1.0; 
  type = OBIT_double;
  ObitInfoListGetTest(in->myDesc->info, "STK_1", &type, dim, 
		       (gpointer)&InfoReal);
  if (type==OBIT_double) in->stk1 = InfoReal.dbl;
  if (type==OBIT_float)  in->stk1 = InfoReal.flt;
   /* npoly */
  if (!ObitInfoListGet(in->myDesc->info, "NPOLY", &type, dim, 
		       (gpointer)&in->npoly, err)) return;
   /* tabrev */
  in->tabrev = 2; 
  ObitInfoListGetTest(in->myDesc->info, "TABREV", &type, dim, 
		       (gpointer)&in->tabrev);
  in->revision = 2.0; 
  InfoReal.dbl = 2.0; 
  type = OBIT_double;
  ObitInfoListGetTest(in->myDesc->info, "REVISION", &type, dim, 
		       (gpointer)&InfoReal);
  if (type==OBIT_double) in->revision = InfoReal.dbl;
  if (type==OBIT_float)  in->revision = InfoReal.flt;

  /* initialize column numbers/offsets */
  in->TimeOff = -1;
  in->TimeCol = -1;
  in->TimeIOff = -1;
  in->TimeICol = -1;
  in->SourIDOff = -1;
  in->SourIDCol = -1;
  in->antennaNoOff = -1;
  in->antennaNoCol = -1;
  in->ArrayOff = -1;
  in->ArrayCol = -1;
  in->FreqIDOff = -1;
  in->FreqIDCol = -1;
  in->IFROff = -1;
  in->IFRCol = -1;
  in->FreqVarOff = -1;
  in->FreqVarCol = -1;
  in->PDelay1Off = -1;
  in->PDelay1Col = -1;
  in->GDelay1Off = -1;
  in->GDelay1Col = -1;
  in->PRate1Off = -1;
  in->PRate1Col = -1;
  in->GRate1Off = -1;
  in->GRate1Col = -1;
  in->Disp1Off = -1;
  in->Disp1Col = -1;
  in->DRate1Off = -1;
  in->DRate1Col = -1;
  in->PDelay2Off = -1;
  in->PDelay2Col = -1;
  in->GDelay2Off = -1;
  in->GDelay2Col = -1;
  in->PRate2Off = -1;
  in->PRate2Col = -1;
  in->GRate2Off = -1;
  in->GRate2Col = -1;
  in->Disp2Off = -1;
  in->Disp2Col = -1;
  in->DRate2Off = -1;
  in->DRate2Col = -1;
  /* Find columns and set offsets */
  desc = in->myDesc;
  if (desc->FieldName) {
    for (i=0; i<desc->nfield; i++) {
      if (!strncmp (desc->FieldName[i], "TIME    ", 8)) {
	 in->TimeOff = desc->offset[i];
 	 in->TimeCol = i;
      }
      if (!strncmp (desc->FieldName[i], "TIME_INTERVAL ", 14)) {
	 in->TimeIOff = desc->offset[i];
 	 in->TimeICol = i;
      }
      if (!strncmp (desc->FieldName[i], "SOURCE_ID  ", 11)) {
	 in->SourIDOff = desc->offset[i];
 	 in->SourIDCol = i;
      }
      if (!strncmp (desc->FieldName[i], "ANTENNA_NO", 10)) {
	 in->antennaNoOff = desc->offset[i];
 	 in->antennaNoCol = i;
      }
      if (!strncmp (desc->FieldName[i], "ARRAY      ", 11)) {
	 in->ArrayOff = desc->offset[i];
 	 in->ArrayCol = i;
      }
      if (!strncmp (desc->FieldName[i], "FREQID", 6)) {
	 in->FreqIDOff = desc->offset[i];
 	 in->FreqIDCol = i;
      }
      if (!strncmp (desc->FieldName[i], "I.FAR.ROT", 9)) {
	 in->IFROff = desc->offset[i];
 	 in->IFRCol = i;
      }
      if (!strncmp (desc->FieldName[i], "FREQ.VAR", 8)) {
	 in->FreqVarOff = desc->offset[i];
 	 in->FreqVarCol = i;
      }
      if (!strncmp (desc->FieldName[i], "PDELAY_1", 8)) {
	     in->PDelay1Off = desc->offset[i];
 	     in->PDelay1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "GDELAY_1", 8)) {
	     in->GDelay1Off = desc->offset[i];
 	     in->GDelay1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "PRATE_1", 7)) {
	     in->PRate1Off = desc->offset[i];
 	     in->PRate1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "GRATE_1", 7)) {
	     in->GRate1Off = desc->offset[i];
 	     in->GRate1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "DISP_1", 6)) {
	     in->Disp1Off = desc->offset[i];
 	     in->Disp1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "DDISP_1", 7)) {
	     in->DRate1Off = desc->offset[i];
 	     in->DRate1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "PDELAY_2", 8)) {
	     in->PDelay2Off = desc->offset[i];
 	     in->PDelay2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "GDELAY_2", 8)) {
	     in->GDelay2Off = desc->offset[i];
 	     in->GDelay2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "PRATE_2", 7)) {
	     in->PRate2Off = desc->offset[i];
 	     in->PRate2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "GRATE_2", 7)) {
	     in->GRate2Off = desc->offset[i];
 	     in->GRate2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "DISP_2", 6)) {
	     in->Disp2Off = desc->offset[i];
 	     in->Disp2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "DDISP_2", 7)) {
	     in->DRate2Off = desc->offset[i];
 	     in->DRate2Col = i;
      }
     }
  }

  /* Check required columns */
  Obit_return_if_fail((in->TimeOff > -1), err,
       "ObitTableIMUpdate: Could not find column Time");
  Obit_return_if_fail((in->TimeIOff > -1), err,
       "ObitTableIMUpdate: Could not find column TimeI");
  Obit_return_if_fail((in->SourIDOff > -1), err,
       "ObitTableIMUpdate: Could not find column SourID");
  Obit_return_if_fail((in->antennaNoOff > -1), err,
       "ObitTableIMUpdate: Could not find column antennaNo");
  Obit_return_if_fail((in->ArrayOff > -1), err,
       "ObitTableIMUpdate: Could not find column Array");
  Obit_return_if_fail((in->FreqIDOff > -1), err,
       "ObitTableIMUpdate: Could not find column FreqID");
  Obit_return_if_fail((in->IFROff > -1), err,
       "ObitTableIMUpdate: Could not find column IFR");
  Obit_return_if_fail((in->FreqVarOff > -1), err,
       "ObitTableIMUpdate: Could not find column FreqVar");
  if (in->numPol>=1) {
    Obit_return_if_fail((in->PDelay1Off > -1), err,
       "ObitTableIMUpdate: Could not find column PDelay1");
    Obit_return_if_fail((in->GDelay1Off > -1), err,
       "ObitTableIMUpdate: Could not find column GDelay1");
    Obit_return_if_fail((in->PRate1Off > -1), err,
       "ObitTableIMUpdate: Could not find column PRate1");
    Obit_return_if_fail((in->GRate1Off > -1), err,
       "ObitTableIMUpdate: Could not find column GRate1");
    Obit_return_if_fail((in->Disp1Off > -1), err,
       "ObitTableIMUpdate: Could not find column Disp1");
  } /* end of if index in range */
  if (in->numPol>=2) {
    Obit_return_if_fail((in->PDelay2Off > -1), err,
       "ObitTableIMUpdate: Could not find column PDelay2");
    Obit_return_if_fail((in->GDelay2Off > -1), err,
       "ObitTableIMUpdate: Could not find column GDelay2");
    Obit_return_if_fail((in->PRate2Off > -1), err,
       "ObitTableIMUpdate: Could not find column PRate2");
    Obit_return_if_fail((in->GRate2Off > -1), err,
       "ObitTableIMUpdate: Could not find column GRate2");
    Obit_return_if_fail((in->Disp2Off > -1), err,
       "ObitTableIMUpdate: Could not find column Disp2");
  } /* end of if index in range */
} /* end ObitTableIMUpdate */

/**
 * Copy table specific (keyword) information  to infolist.
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableIMDumpKey (ObitTableIM *in, ObitErr *err)
{
  ObitInfoList *info=NULL;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM] = {1,1,1,1,1};

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Set Keywords */
  if (in->myIO!=NULL) info = ((ObitTableDesc*)(in->myIO->myDesc))->info;
  else info = in->myDesc->info;
  /* RefDate */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEIM;
  ObitInfoListAlwaysPut(info, "RDATE", type, dim, 
		  (gpointer)&in->RefDate);
  /* numPol */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_POL", type, dim, 
		  (gpointer)&in->numPol);
  /* obscode */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEIM;
  ObitInfoListAlwaysPut(info, "OBSCODE", type, dim, 
		  (gpointer)&in->obscode);
  /* numStkd */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_STKD", type, dim, 
		  (gpointer)&in->numStkd);
  /* numBand */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_BAND", type, dim, 
		  (gpointer)&in->numBand);
  /* numChan */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_CHAN", type, dim, 
		  (gpointer)&in->numChan);
  /* refFreq */
  type  = OBIT_double;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "REF_FREQ", type, dim, 
		  (gpointer)&in->refFreq);
  /* chanBW */
  type  = OBIT_float;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "CHAN_BW", type, dim, 
		  (gpointer)&in->chanBW);
  /* refPixl */
  type  = OBIT_float;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "REF_PIXL", type, dim, 
		  (gpointer)&in->refPixl);
  /* stk1 */
  type  = OBIT_float;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "STK_1", type, dim, 
		  (gpointer)&in->stk1);
  /* npoly */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NPOLY", type, dim, 
		  (gpointer)&in->npoly);
  /* tabrev */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "TABREV", type, dim, 
		  (gpointer)&in->tabrev);
  /* revision */
  type  = OBIT_float;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "REVISION", type, dim, 
		  (gpointer)&in->revision);
   
} /* end ObitTableIMDumpKey */
