/**
 * Get table specific information from the infolist or descriptor
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableMCUpdate (ObitTableMC *in, ObitErr *err)
{
  olong i;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM];
  ObitTableDesc *desc;
   union ObitInfoListEquiv InfoReal; 

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Get Keywords */
   /* obscode */
  strncpy (in->obscode, "AA000", MAXKEYCHARTABLEMC); 
  ObitInfoListGetTest(in->myDesc->info, "OBSCODE", &type, dim, 
		       (gpointer)&in->obscode);
   /* numPol */
  if (!ObitInfoListGet(in->myDesc->info, "NO_POL", &type, dim, 
		       (gpointer)&in->numPol, err)) return;
   /* RefDate */
  strncpy (in->RefDate, "YYYYMMDD", MAXKEYCHARTABLEMC); 
  ObitInfoListGetTest(in->myDesc->info, "RDATE", &type, dim, 
		       (gpointer)&in->RefDate);
   /* numStkd */
  if (!ObitInfoListGet(in->myDesc->info, "NO_STKD", &type, dim, 
		       (gpointer)&in->numStkd, err)) return;
   /* stk1 */
  if (!ObitInfoListGet(in->myDesc->info, "STK_1", &type, dim, 
		       (gpointer)&in->stk1, err)) return;
   /* numBand */
  if (!ObitInfoListGet(in->myDesc->info, "NO_BAND", &type, dim, 
		       (gpointer)&in->numBand, err)) return;
   /* numChan */
  if (!ObitInfoListGet(in->myDesc->info, "NO_CHAN", &type, dim, 
		       (gpointer)&in->numChan, err)) return;
   /* refFreq */
  if (!ObitInfoListGet(in->myDesc->info, "REF_FREQ", &type, dim, 
		       (gpointer)&InfoReal, err)) return;
  if (type==OBIT_double) in->refFreq = InfoReal.dbl;
  if (type==OBIT_float)  in->refFreq = InfoReal.flt;
   /* chanBW */
  if (!ObitInfoListGet(in->myDesc->info, "CHAN_BW", &type, dim, 
		       (gpointer)&InfoReal, err)) return;
  if (type==OBIT_double) in->chanBW = InfoReal.dbl;
  if (type==OBIT_float)  in->chanBW = InfoReal.flt;
   /* refPixl */
  if (!ObitInfoListGet(in->myDesc->info, "REF_PIXL", &type, dim, 
		       (gpointer)&InfoReal, err)) return;
  if (type==OBIT_double) in->refPixl = InfoReal.dbl;
  if (type==OBIT_float)  in->refPixl = InfoReal.flt;
   /* FFTSize */
  if (!ObitInfoListGet(in->myDesc->info, "FFT_SIZE", &type, dim, 
		       (gpointer)&in->FFTSize, err)) return;
   /* oversamp */
  if (!ObitInfoListGet(in->myDesc->info, "OVERSAMP", &type, dim, 
		       (gpointer)&in->oversamp, err)) return;
   /* zeroPad */
  if (!ObitInfoListGet(in->myDesc->info, "ZERO_PAD", &type, dim, 
		       (gpointer)&in->zeroPad, err)) return;
   /* taperFn */
  if (!ObitInfoListGet(in->myDesc->info, "TAPER_FN", &type, dim, 
		       (gpointer)&in->taperFn, err)) return;
   /* revision */
  in->revision = 1; 
  ObitInfoListGetTest(in->myDesc->info, "TABREV", &type, dim, 
		       (gpointer)&in->revision);

  /* initialize column numbers/offsets */
  in->TimeOff = -1;
  in->TimeCol = -1;
  in->SourIDOff = -1;
  in->SourIDCol = -1;
  in->antennaNoOff = -1;
  in->antennaNoCol = -1;
  in->ArrayOff = -1;
  in->ArrayCol = -1;
  in->FreqIDOff = -1;
  in->FreqIDCol = -1;
  in->atmosOff = -1;
  in->atmosCol = -1;
  in->DatmosOff = -1;
  in->DatmosCol = -1;
  in->GDelayOff = -1;
  in->GDelayCol = -1;
  in->GRateOff = -1;
  in->GRateCol = -1;
  in->clock1Off = -1;
  in->clock1Col = -1;
  in->Dclock1Off = -1;
  in->Dclock1Col = -1;
  in->LOOffset1Off = -1;
  in->LOOffset1Col = -1;
  in->DLOOffset1Off = -1;
  in->DLOOffset1Col = -1;
  in->disp1Off = -1;
  in->disp1Col = -1;
  in->Ddisp1Off = -1;
  in->Ddisp1Col = -1;
  in->clock2Off = -1;
  in->clock2Col = -1;
  in->Dclock2Off = -1;
  in->Dclock2Col = -1;
  in->LOOffset2Off = -1;
  in->LOOffset2Col = -1;
  in->DLOOffset2Off = -1;
  in->DLOOffset2Col = -1;
  in->disp2Off = -1;
  in->disp2Col = -1;
  in->Ddisp2Off = -1;
  in->Ddisp2Col = -1;
  /* Find columns and set offsets */
  desc = in->myDesc;
  if (desc->FieldName) {
    for (i=0; i<desc->nfield; i++) {
      if (!strncmp (desc->FieldName[i], "TIME    ", 8)) {
	 in->TimeOff = desc->offset[i];
 	 in->TimeCol = i;
      }
      if (!strncmp (desc->FieldName[i], "SOURCE_ID  ", 11)) {
	 in->SourIDOff = desc->offset[i];
 	 in->SourIDCol = i;
      }
      if (!strncmp (desc->FieldName[i], "ANTENNA_NO", 10)) {
	 in->antennaNoOff = desc->offset[i];
 	 in->antennaNoCol = i;
      }
      if (!strncmp (desc->FieldName[i], "ARRAY      ", 11)) {
	 in->ArrayOff = desc->offset[i];
 	 in->ArrayCol = i;
      }
      if (!strncmp (desc->FieldName[i], "FREQID", 6)) {
	 in->FreqIDOff = desc->offset[i];
 	 in->FreqIDCol = i;
      }
      if (!strncmp (desc->FieldName[i], "ATMOS  ", 7)) {
	 in->atmosOff = desc->offset[i];
 	 in->atmosCol = i;
      }
      if (!strncmp (desc->FieldName[i], "DATMOS  ", 8)) {
	 in->DatmosOff = desc->offset[i];
 	 in->DatmosCol = i;
      }
      if (!strncmp (desc->FieldName[i], "GDELAY", 6)) {
	 in->GDelayOff = desc->offset[i];
 	 in->GDelayCol = i;
      }
      if (!strncmp (desc->FieldName[i], "GRATE ", 6)) {
	 in->GRateOff = desc->offset[i];
 	 in->GRateCol = i;
      }
      if (!strncmp (desc->FieldName[i], "CLOCK_1", 7)) {
	     in->clock1Off = desc->offset[i];
 	     in->clock1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "DCLOCK_1", 8)) {
	     in->Dclock1Off = desc->offset[i];
 	     in->Dclock1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "LO_OFFSET_1", 11)) {
	     in->LOOffset1Off = desc->offset[i];
 	     in->LOOffset1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "DLO_OFFSET_1", 12)) {
	     in->DLOOffset1Off = desc->offset[i];
 	     in->DLOOffset1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "DISP_1", 6)) {
	     in->disp1Off = desc->offset[i];
 	     in->disp1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "DDISP_1", 7)) {
	     in->Ddisp1Off = desc->offset[i];
 	     in->Ddisp1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "CLOCK_2", 7)) {
	     in->clock2Off = desc->offset[i];
 	     in->clock2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "DCLOCK_2", 8)) {
	     in->Dclock2Off = desc->offset[i];
 	     in->Dclock2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "LO_OFFSET_2", 11)) {
	     in->LOOffset2Off = desc->offset[i];
 	     in->LOOffset2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "DLO_OFFSET_2", 12)) {
	     in->DLOOffset2Off = desc->offset[i];
 	     in->DLOOffset2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "DISP_2", 6)) {
	     in->disp2Off = desc->offset[i];
 	     in->disp2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "DDISP_2", 7)) {
	     in->Ddisp2Off = desc->offset[i];
 	     in->Ddisp2Col = i;
      }
     }
  }

  /* Check required columns */
  Obit_return_if_fail((in->TimeOff > -1), err,
       "ObitTableMCUpdate: Could not find column Time");
  Obit_return_if_fail((in->SourIDOff > -1), err,
       "ObitTableMCUpdate: Could not find column SourID");
  Obit_return_if_fail((in->antennaNoOff > -1), err,
       "ObitTableMCUpdate: Could not find column antennaNo");
  Obit_return_if_fail((in->ArrayOff > -1), err,
       "ObitTableMCUpdate: Could not find column Array");
  Obit_return_if_fail((in->FreqIDOff > -1), err,
       "ObitTableMCUpdate: Could not find column FreqID");
  Obit_return_if_fail((in->atmosOff > -1), err,
       "ObitTableMCUpdate: Could not find column atmos");
  Obit_return_if_fail((in->DatmosOff > -1), err,
       "ObitTableMCUpdate: Could not find column Datmos");
  Obit_return_if_fail((in->GDelayOff > -1), err,
       "ObitTableMCUpdate: Could not find column GDelay");
  Obit_return_if_fail((in->GRateOff > -1), err,
       "ObitTableMCUpdate: Could not find column GRate");
  if (in->numPol>=1) {
    Obit_return_if_fail((in->clock1Off > -1), err,
       "ObitTableMCUpdate: Could not find column clock1");
    Obit_return_if_fail((in->Dclock1Off > -1), err,
       "ObitTableMCUpdate: Could not find column Dclock1");
    Obit_return_if_fail((in->LOOffset1Off > -1), err,
       "ObitTableMCUpdate: Could not find column LOOffset1");
    Obit_return_if_fail((in->DLOOffset1Off > -1), err,
       "ObitTableMCUpdate: Could not find column DLOOffset1");
    Obit_return_if_fail((in->disp1Off > -1), err,
       "ObitTableMCUpdate: Could not find column disp1");
  } /* end of if index in range */
  if (in->numPol>=2) {
    Obit_return_if_fail((in->clock2Off > -1), err,
       "ObitTableMCUpdate: Could not find column clock2");
    Obit_return_if_fail((in->Dclock2Off > -1), err,
       "ObitTableMCUpdate: Could not find column Dclock2");
    Obit_return_if_fail((in->LOOffset2Off > -1), err,
       "ObitTableMCUpdate: Could not find column LOOffset2");
    Obit_return_if_fail((in->DLOOffset2Off > -1), err,
       "ObitTableMCUpdate: Could not find column DLOOffset2");
    Obit_return_if_fail((in->disp2Off > -1), err,
       "ObitTableMCUpdate: Could not find column disp2");
  } /* end of if index in range */
} /* end ObitTableMCUpdate */

/**
 * Copy table specific (keyword) information  to infolist.
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableMCDumpKey (ObitTableMC *in, ObitErr *err)
{
  ObitInfoList *info=NULL;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM] = {1,1,1,1,1};

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Set Keywords */
  if (in->myIO!=NULL) info = ((ObitTableDesc*)(in->myIO->myDesc))->info;
  else info = in->myDesc->info;
  /* obscode */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEMC;
  ObitInfoListAlwaysPut(info, "OBSCODE", type, dim, 
		  (gpointer)&in->obscode);
  /* numPol */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_POL", type, dim, 
		  (gpointer)&in->numPol);
  /* RefDate */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEMC;
  ObitInfoListAlwaysPut(info, "RDATE", type, dim, 
		  (gpointer)&in->RefDate);
  /* numStkd */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_STKD", type, dim, 
		  (gpointer)&in->numStkd);
  /* stk1 */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "STK_1", type, dim, 
		  (gpointer)&in->stk1);
  /* numBand */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_BAND", type, dim, 
		  (gpointer)&in->numBand);
  /* numChan */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_CHAN", type, dim, 
		  (gpointer)&in->numChan);
  /* refFreq */
  type  = OBIT_double;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "REF_FREQ", type, dim, 
		  (gpointer)&in->refFreq);
  /* chanBW */
  type  = OBIT_float;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "CHAN_BW", type, dim, 
		  (gpointer)&in->chanBW);
  /* refPixl */
  type  = OBIT_float;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "REF_PIXL", type, dim, 
		  (gpointer)&in->refPixl);
  /* FFTSize */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "FFT_SIZE", type, dim, 
		  (gpointer)&in->FFTSize);
  /* oversamp */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "OVERSAMP", type, dim, 
		  (gpointer)&in->oversamp);
  /* zeroPad */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "ZERO_PAD", type, dim, 
		  (gpointer)&in->zeroPad);
  /* taperFn */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEMC;
  ObitInfoListAlwaysPut(info, "TAPER_FN", type, dim, 
		  (gpointer)&in->taperFn);
  /* revision */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "TABREV", type, dim, 
		  (gpointer)&in->revision);
   
} /* end ObitTableMCDumpKey */
