/**
 * Get table specific information from the infolist or descriptor
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableNIUpdate (ObitTableNI *in, ObitErr *err)
{
  olong i;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM];
  ObitTableDesc *desc;
   union ObitInfoListEquiv InfoReal; 

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Get Keywords */
   /* revision */
  in->revision = 1; 
  ObitInfoListGetTest(in->myDesc->info, "REVISION", &type, dim, 
		       (gpointer)&in->revision);
   /* numCoef */
  in->numCoef = 5; 
  ObitInfoListGetTest(in->myDesc->info, "NUM_COEF", &type, dim, 
		       (gpointer)&in->numCoef);
  in->heightIon = 1.0e10; 
  InfoReal.dbl = 1.0e10; 
  type = OBIT_double;
  ObitInfoListGetTest(in->myDesc->info, "H_ION", &type, dim, 
		       (gpointer)&InfoReal);
  if (type==OBIT_double) in->heightIon = InfoReal.dbl;
  if (type==OBIT_float)  in->heightIon = InfoReal.flt;
   /* refFreq */
  if (!ObitInfoListGet(in->myDesc->info, "REF_FREQ", &type, dim, 
		       (gpointer)&InfoReal, err)) return;
  if (type==OBIT_double) in->refFreq = InfoReal.dbl;
  if (type==OBIT_float)  in->refFreq = InfoReal.flt;

  /* initialize column numbers/offsets */
  in->TimeOff = -1;
  in->TimeCol = -1;
  in->TimeIOff = -1;
  in->TimeICol = -1;
  in->antNoOff = -1;
  in->antNoCol = -1;
  in->SourIdOff = -1;
  in->SourIdCol = -1;
  in->SubAOff = -1;
  in->SubACol = -1;
  in->weightOff = -1;
  in->weightCol = -1;
  in->coefOff = -1;
  in->coefCol = -1;
  /* Find columns and set offsets */
  desc = in->myDesc;
  if (desc->FieldName) {
    for (i=0; i<desc->nfield; i++) {
      if (!strncmp (desc->FieldName[i], "TIME    ", 8)) {
	 in->TimeOff = desc->offset[i];
 	 in->TimeCol = i;
      }
      if (!strncmp (desc->FieldName[i], "TIME INTERVAL", 13)) {
	 in->TimeIOff = desc->offset[i];
 	 in->TimeICol = i;
      }
      if (!strncmp (desc->FieldName[i], "ANTENNA NO.", 11)) {
	 in->antNoOff = desc->offset[i];
 	 in->antNoCol = i;
      }
      if (!strncmp (desc->FieldName[i], "SOURCE ID", 9)) {
	 in->SourIdOff = desc->offset[i];
 	 in->SourIdCol = i;
      }
      if (!strncmp (desc->FieldName[i], "SUBARRAY", 8)) {
	 in->SubAOff = desc->offset[i];
 	 in->SubACol = i;
      }
      if (!strncmp (desc->FieldName[i], "WEIGHT ", 7)) {
	 in->weightOff = desc->offset[i];
 	 in->weightCol = i;
      }
      if (!strncmp (desc->FieldName[i], "COEF   ", 7)) {
	 in->coefOff = desc->offset[i];
 	 in->coefCol = i;
      }
     }
  }

  /* Check required columns */
  Obit_return_if_fail((in->TimeOff > -1), err,
       "ObitTableNIUpdate: Could not find column Time");
  Obit_return_if_fail((in->TimeIOff > -1), err,
       "ObitTableNIUpdate: Could not find column TimeI");
  Obit_return_if_fail((in->antNoOff > -1), err,
       "ObitTableNIUpdate: Could not find column antNo");
  Obit_return_if_fail((in->SourIdOff > -1), err,
       "ObitTableNIUpdate: Could not find column SourId");
  Obit_return_if_fail((in->SubAOff > -1), err,
       "ObitTableNIUpdate: Could not find column SubA");
  Obit_return_if_fail((in->weightOff > -1), err,
       "ObitTableNIUpdate: Could not find column weight");
} /* end ObitTableNIUpdate */

/**
 * Copy table specific (keyword) information  to infolist.
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableNIDumpKey (ObitTableNI *in, ObitErr *err)
{
  ObitInfoList *info=NULL;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM] = {1,1,1,1,1};

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Set Keywords */
  if (in->myIO!=NULL) info = ((ObitTableDesc*)(in->myIO->myDesc))->info;
  else info = in->myDesc->info;
  /* revision */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "REVISION", type, dim, 
		  (gpointer)&in->revision);
  /* numCoef */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NUM_COEF", type, dim, 
		  (gpointer)&in->numCoef);
  /* heightIon */
  type  = OBIT_float;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "H_ION", type, dim, 
		  (gpointer)&in->heightIon);
  /* refFreq */
  type  = OBIT_double;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "REF_FREQ", type, dim, 
		  (gpointer)&in->refFreq);
   
} /* end ObitTableNIDumpKey */
