/**
 * Get table specific information from the infolist or descriptor
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTablePTUpdate (ObitTablePT *in, ObitErr *err)
{
  olong i;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM];
  ObitTableDesc *desc;
   

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Get Keywords */
   /* revision */
  in->revision = 1; 
  ObitInfoListGetTest(in->myDesc->info, "TABREV", &type, dim, 
		       (gpointer)&in->revision);
   /* numTerm */
  in->numTerm = 0; 
  ObitInfoListGetTest(in->myDesc->info, "NO_TERM", &type, dim, 
		       (gpointer)&in->numTerm);
   /* RefDate */
  strncpy (in->RefDate, "YYYYMMDD", MAXKEYCHARTABLEPT); 
  ObitInfoListGetTest(in->myDesc->info, "RDATE", &type, dim, 
		       (gpointer)&in->RefDate);

  /* initialize column numbers/offsets */
  in->TimeOff = -1;
  in->TimeCol = -1;
  in->TimeIOff = -1;
  in->TimeICol = -1;
  in->antNoOff = -1;
  in->antNoCol = -1;
  in->EncoderOff = -1;
  in->EncoderCol = -1;
  in->pointingTrackingOff = -1;
  in->pointingTrackingCol = -1;
  in->usePolynomialsOff = -1;
  in->usePolynomialsCol = -1;
  in->timeOriginOff = -1;
  in->timeOriginCol = -1;
  in->PointingDirectionOff = -1;
  in->PointingDirectionCol = -1;
  in->targetOff = -1;
  in->targetCol = -1;
  in->offsetOff = -1;
  in->offsetCol = -1;
  in->pointingModelIdOff = -1;
  in->pointingModelIdCol = -1;
  in->overTheTopOff = -1;
  in->overTheTopCol = -1;
  in->SourceOffsetOff = -1;
  in->SourceOffsetCol = -1;
  in->SourceReferenceCodeOffsetOff = -1;
  in->SourceReferenceCodeOffsetCol = -1;
  in->sourceEquinoxOffsetOff = -1;
  in->sourceEquinoxOffsetCol = -1;
  in->sampledTimeIntervalOff = -1;
  in->sampledTimeIntervalCol = -1;
  in->AtmosphericCorrectionOff = -1;
  in->AtmosphericCorrectionCol = -1;
  /* Find columns and set offsets */
  desc = in->myDesc;
  if (desc->FieldName) {
    for (i=0; i<desc->nfield; i++) {
      if (!strncmp (desc->FieldName[i], "TIME    ", 8)) {
	 in->TimeOff = desc->offset[i];
 	 in->TimeCol = i;
      }
      if (!strncmp (desc->FieldName[i], "TIME INTERVAL", 13)) {
	 in->TimeIOff = desc->offset[i];
 	 in->TimeICol = i;
      }
      if (!strncmp (desc->FieldName[i], "ANTENNA NO.", 11)) {
	 in->antNoOff = desc->offset[i];
 	 in->antNoCol = i;
      }
      if (!strncmp (desc->FieldName[i], "ENCODER", 7)) {
	 in->EncoderOff = desc->offset[i];
 	 in->EncoderCol = i;
      }
      if (!strncmp (desc->FieldName[i], "POINTINGTRACKING", 16)) {
	 in->pointingTrackingOff = desc->offset[i];
 	 in->pointingTrackingCol = i;
      }
      if (!strncmp (desc->FieldName[i], "USEPOLYNOMIALS", 14)) {
	 in->usePolynomialsOff = desc->offset[i];
 	 in->usePolynomialsCol = i;
      }
      if (!strncmp (desc->FieldName[i], "TIMEORIGIN", 10)) {
	 in->timeOriginOff = desc->offset[i];
 	 in->timeOriginCol = i;
      }
      if (!strncmp (desc->FieldName[i], "POINTINGDIRECTION", 17)) {
	 in->PointingDirectionOff = desc->offset[i];
 	 in->PointingDirectionCol = i;
      }
      if (!strncmp (desc->FieldName[i], "TARGET", 6)) {
	 in->targetOff = desc->offset[i];
 	 in->targetCol = i;
      }
      if (!strncmp (desc->FieldName[i], "OFFSET", 6)) {
	 in->offsetOff = desc->offset[i];
 	 in->offsetCol = i;
      }
      if (!strncmp (desc->FieldName[i], "POINTINGMODELID", 15)) {
	 in->pointingModelIdOff = desc->offset[i];
 	 in->pointingModelIdCol = i;
      }
      if (!strncmp (desc->FieldName[i], "OVERTHETOP", 10)) {
	 in->overTheTopOff = desc->offset[i];
 	 in->overTheTopCol = i;
      }
      if (!strncmp (desc->FieldName[i], "SOURCEOFFSET", 12)) {
	 in->SourceOffsetOff = desc->offset[i];
 	 in->SourceOffsetCol = i;
      }
      if (!strncmp (desc->FieldName[i], "SOURCEREFERENCECODEOFF", 22)) {
	 in->SourceReferenceCodeOffsetOff = desc->offset[i];
 	 in->SourceReferenceCodeOffsetCol = i;
      }
      if (!strncmp (desc->FieldName[i], "SOURCEEQUINOXOFFSET", 19)) {
	 in->sourceEquinoxOffsetOff = desc->offset[i];
 	 in->sourceEquinoxOffsetCol = i;
      }
      if (!strncmp (desc->FieldName[i], "SAMPLEDTIMEINTERVAL", 19)) {
	 in->sampledTimeIntervalOff = desc->offset[i];
 	 in->sampledTimeIntervalCol = i;
      }
      if (!strncmp (desc->FieldName[i], "ATMOSPHERICCORRECTION", 21)) {
	 in->AtmosphericCorrectionOff = desc->offset[i];
 	 in->AtmosphericCorrectionCol = i;
      }
     }
  }

  /* Check required columns */
  Obit_return_if_fail((in->TimeOff > -1), err,
       "ObitTablePTUpdate: Could not find column Time");
  Obit_return_if_fail((in->TimeIOff > -1), err,
       "ObitTablePTUpdate: Could not find column TimeI");
  Obit_return_if_fail((in->antNoOff > -1), err,
       "ObitTablePTUpdate: Could not find column antNo");
  Obit_return_if_fail((in->EncoderOff > -1), err,
       "ObitTablePTUpdate: Could not find column Encoder");
  Obit_return_if_fail((in->pointingTrackingOff > -1), err,
       "ObitTablePTUpdate: Could not find column pointingTracking");
  Obit_return_if_fail((in->usePolynomialsOff > -1), err,
       "ObitTablePTUpdate: Could not find column usePolynomials");
  Obit_return_if_fail((in->timeOriginOff > -1), err,
       "ObitTablePTUpdate: Could not find column timeOrigin");
  Obit_return_if_fail((in->PointingDirectionOff > -1), err,
       "ObitTablePTUpdate: Could not find column PointingDirection");
  Obit_return_if_fail((in->targetOff > -1), err,
       "ObitTablePTUpdate: Could not find column target");
  Obit_return_if_fail((in->offsetOff > -1), err,
       "ObitTablePTUpdate: Could not find column offset");
  Obit_return_if_fail((in->pointingModelIdOff > -1), err,
       "ObitTablePTUpdate: Could not find column pointingModelId");
} /* end ObitTablePTUpdate */

/**
 * Copy table specific (keyword) information  to infolist.
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTablePTDumpKey (ObitTablePT *in, ObitErr *err)
{
  ObitInfoList *info=NULL;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM] = {1,1,1,1,1};

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Set Keywords */
  if (in->myIO!=NULL) info = ((ObitTableDesc*)(in->myIO->myDesc))->info;
  else info = in->myDesc->info;
  /* revision */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "TABREV", type, dim, 
		  (gpointer)&in->revision);
  /* numTerm */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_TERM", type, dim, 
		  (gpointer)&in->numTerm);
  /* RefDate */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEPT;
  ObitInfoListAlwaysPut(info, "RDATE", type, dim, 
		  (gpointer)&in->RefDate);
   
} /* end ObitTablePTDumpKey */
