/**
 * Get table specific information from the infolist or descriptor
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableSUUpdate (ObitTableSU *in, ObitErr *err)
{
  olong i;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM];
  ObitTableDesc *desc;
   

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Get Keywords */
   /* numIF */
  if (!ObitInfoListGet(in->myDesc->info, "NO_IF", &type, dim, 
		       (gpointer)&in->numIF, err)) return;
   /* velType */
  strncpy (in->velType, "LSR", MAXKEYCHARTABLESU); 
  ObitInfoListGetTest(in->myDesc->info, "VELTYP", &type, dim, 
		       (gpointer)&in->velType);
   /* velDef */
  strncpy (in->velDef, "RADIO", MAXKEYCHARTABLESU); 
  ObitInfoListGetTest(in->myDesc->info, "VELDEF", &type, dim, 
		       (gpointer)&in->velDef);
   /* FreqID */
  in->FreqID = 0; 
  ObitInfoListGetTest(in->myDesc->info, "FREQID", &type, dim, 
		       (gpointer)&in->FreqID);

  /* initialize column numbers/offsets */
  in->SourIDOff = -1;
  in->SourIDCol = -1;
  in->SourceOff = -1;
  in->SourceCol = -1;
  in->QualOff = -1;
  in->QualCol = -1;
  in->CalCodeOff = -1;
  in->CalCodeCol = -1;
  in->IFluxOff = -1;
  in->IFluxCol = -1;
  in->QFluxOff = -1;
  in->QFluxCol = -1;
  in->UFluxOff = -1;
  in->UFluxCol = -1;
  in->VFluxOff = -1;
  in->VFluxCol = -1;
  in->FreqOffOff = -1;
  in->FreqOffCol = -1;
  in->BandwidthOff = -1;
  in->BandwidthCol = -1;
  in->RAMeanOff = -1;
  in->RAMeanCol = -1;
  in->DecMeanOff = -1;
  in->DecMeanCol = -1;
  in->EpochOff = -1;
  in->EpochCol = -1;
  in->RAAppOff = -1;
  in->RAAppCol = -1;
  in->DecAppOff = -1;
  in->DecAppCol = -1;
  in->LSRVelOff = -1;
  in->LSRVelCol = -1;
  in->RestFreqOff = -1;
  in->RestFreqCol = -1;
  in->PMRaOff = -1;
  in->PMRaCol = -1;
  in->PMDecOff = -1;
  in->PMDecCol = -1;
  /* Find columns and set offsets */
  desc = in->myDesc;
  if (desc->FieldName) {
    for (i=0; i<desc->nfield; i++) {
      if (!strncmp (desc->FieldName[i], "ID. NO. ", 8)) {
	 in->SourIDOff = desc->offset[i];
 	 in->SourIDCol = i;
      }
      if (!strncmp (desc->FieldName[i], "SOURCE  ", 8)) {
	 in->SourceOff = desc->offset[i];
 	 in->SourceCol = i;
      }
      if (!strncmp (desc->FieldName[i], "QUAL    ", 8)) {
	 in->QualOff = desc->offset[i];
 	 in->QualCol = i;
      }
      if (!strncmp (desc->FieldName[i], "CALCODE ", 8)) {
	 in->CalCodeOff = desc->offset[i];
 	 in->CalCodeCol = i;
      }
      if (!strncmp (desc->FieldName[i], "IFLUX   ", 8)) {
	 in->IFluxOff = desc->offset[i];
 	 in->IFluxCol = i;
      }
      if (!strncmp (desc->FieldName[i], "QFLUX   ", 8)) {
	 in->QFluxOff = desc->offset[i];
 	 in->QFluxCol = i;
      }
      if (!strncmp (desc->FieldName[i], "UFLUX   ", 8)) {
	 in->UFluxOff = desc->offset[i];
 	 in->UFluxCol = i;
      }
      if (!strncmp (desc->FieldName[i], "VFLUX   ", 8)) {
	 in->VFluxOff = desc->offset[i];
 	 in->VFluxCol = i;
      }
      if (!strncmp (desc->FieldName[i], "FREQOFF ", 8)) {
	 in->FreqOffOff = desc->offset[i];
 	 in->FreqOffCol = i;
      }
      if (!strncmp (desc->FieldName[i], "BANDWIDTH", 9)) {
	 in->BandwidthOff = desc->offset[i];
 	 in->BandwidthCol = i;
      }
      if (!strncmp (desc->FieldName[i], "RAEPO   ", 8)) {
	 in->RAMeanOff = desc->offset[i];
 	 in->RAMeanCol = i;
      }
      if (!strncmp (desc->FieldName[i], "DECEPO  ", 8)) {
	 in->DecMeanOff = desc->offset[i];
 	 in->DecMeanCol = i;
      }
      if (!strncmp (desc->FieldName[i], "EPOCH   ", 8)) {
	 in->EpochOff = desc->offset[i];
 	 in->EpochCol = i;
      }
      if (!strncmp (desc->FieldName[i], "RAAPP   ", 8)) {
	 in->RAAppOff = desc->offset[i];
 	 in->RAAppCol = i;
      }
      if (!strncmp (desc->FieldName[i], "DECAPP  ", 8)) {
	 in->DecAppOff = desc->offset[i];
 	 in->DecAppCol = i;
      }
      if (!strncmp (desc->FieldName[i], "LSRVEL ", 7)) {
	 in->LSRVelOff = desc->offset[i];
 	 in->LSRVelCol = i;
      }
      if (!strncmp (desc->FieldName[i], "RESTFREQ", 8)) {
	 in->RestFreqOff = desc->offset[i];
 	 in->RestFreqCol = i;
      }
      if (!strncmp (desc->FieldName[i], "PMRA   ", 7)) {
	 in->PMRaOff = desc->offset[i];
 	 in->PMRaCol = i;
      }
      if (!strncmp (desc->FieldName[i], "PMDEC  ", 7)) {
	 in->PMDecOff = desc->offset[i];
 	 in->PMDecCol = i;
      }
     }
  }

  /* Check required columns */
  Obit_return_if_fail((in->SourIDOff > -1), err,
       "ObitTableSUUpdate: Could not find column SourID");
  Obit_return_if_fail((in->SourceOff > -1), err,
       "ObitTableSUUpdate: Could not find column Source");
  Obit_return_if_fail((in->QualOff > -1), err,
       "ObitTableSUUpdate: Could not find column Qual");
  Obit_return_if_fail((in->CalCodeOff > -1), err,
       "ObitTableSUUpdate: Could not find column CalCode");
  Obit_return_if_fail((in->IFluxOff > -1), err,
       "ObitTableSUUpdate: Could not find column IFlux");
  Obit_return_if_fail((in->QFluxOff > -1), err,
       "ObitTableSUUpdate: Could not find column QFlux");
  Obit_return_if_fail((in->UFluxOff > -1), err,
       "ObitTableSUUpdate: Could not find column UFlux");
  Obit_return_if_fail((in->VFluxOff > -1), err,
       "ObitTableSUUpdate: Could not find column VFlux");
  Obit_return_if_fail((in->FreqOffOff > -1), err,
       "ObitTableSUUpdate: Could not find column FreqOff");
  Obit_return_if_fail((in->BandwidthOff > -1), err,
       "ObitTableSUUpdate: Could not find column Bandwidth");
  Obit_return_if_fail((in->RAMeanOff > -1), err,
       "ObitTableSUUpdate: Could not find column RAMean");
  Obit_return_if_fail((in->DecMeanOff > -1), err,
       "ObitTableSUUpdate: Could not find column DecMean");
  Obit_return_if_fail((in->EpochOff > -1), err,
       "ObitTableSUUpdate: Could not find column Epoch");
  Obit_return_if_fail((in->RAAppOff > -1), err,
       "ObitTableSUUpdate: Could not find column RAApp");
  Obit_return_if_fail((in->DecAppOff > -1), err,
       "ObitTableSUUpdate: Could not find column DecApp");
  Obit_return_if_fail((in->LSRVelOff > -1), err,
       "ObitTableSUUpdate: Could not find column LSRVel");
  Obit_return_if_fail((in->RestFreqOff > -1), err,
       "ObitTableSUUpdate: Could not find column RestFreq");
  Obit_return_if_fail((in->PMRaOff > -1), err,
       "ObitTableSUUpdate: Could not find column PMRa");
} /* end ObitTableSUUpdate */

/**
 * Copy table specific (keyword) information  to infolist.
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableSUDumpKey (ObitTableSU *in, ObitErr *err)
{
  ObitInfoList *info=NULL;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM] = {1,1,1,1,1};

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Set Keywords */
  if (in->myIO!=NULL) info = ((ObitTableDesc*)(in->myIO->myDesc))->info;
  else info = in->myDesc->info;
  /* numIF */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_IF", type, dim, 
		  (gpointer)&in->numIF);
  /* velType */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLESU;
  ObitInfoListAlwaysPut(info, "VELTYP", type, dim, 
		  (gpointer)&in->velType);
  /* velDef */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLESU;
  ObitInfoListAlwaysPut(info, "VELDEF", type, dim, 
		  (gpointer)&in->velDef);
  /* FreqID */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "FREQID", type, dim, 
		  (gpointer)&in->FreqID);
   
} /* end ObitTableSUDumpKey */
