/**
 * Initialize global ClassInfo Function pointers.
 */
static void ObitTableSYClassInfoDefFn (gpointer inClass)
{
  ObitTableSYClassInfo *theClass = (ObitTableSYClassInfo*)inClass;
  ObitClassInfo *ParentClass = (ObitClassInfo*)myClassInfo.ParentClass;

  if (theClass->initialized) return;  /* only once */

  /* Check type of inClass */
  g_assert (ObitInfoIsA(inClass, (ObitClassInfo*)&myClassInfo));

  /* Initialize (recursively) parent class first */
  if ((ParentClass!=NULL) && 
      (ParentClass->ObitClassInfoDefFn!=NULL))
    ParentClass->ObitClassInfoDefFn(theClass);

  /* function pointers defined or overloaded this class */
  theClass->ObitClassInit = (ObitClassInitFP)ObitTableSYClassInit;
  theClass->ObitClassInfoDefFn = (ObitClassInfoDefFnFP)ObitTableSYClassInfoDefFn;
  theClass->ObitGetClass  = (ObitGetClassFP)ObitTableSYGetClass;
  theClass->newObit       = (newObitFP)newObitTableSY;
  theClass->ObitCopy      = (ObitCopyFP)ObitTableSYCopy;
  theClass->ObitClone     = (ObitCloneFP)ObitTableClone;
  theClass->ObitClear     = (ObitClearFP)ObitTableSYClear;
  theClass->ObitInit      = (ObitInitFP)ObitTableSYInit;
  theClass->ObitTableConvert = (ObitTableConvertFP)ObitTableSYConvert;
  theClass->ObitTableOpen    = (ObitTableOpenFP)ObitTableSYOpen;
  theClass->ObitTableClose   = (ObitTableCloseFP)ObitTableSYClose;
  theClass->ObitTableRead    = (ObitTableReadFP)ObitTableRead;
  theClass->ObitTableReadSelect = 
    (ObitTableReadSelectFP)ObitTableReadSelect;
  theClass->ObitTableWrite = (ObitTableWriteFP)ObitTableWrite;
  theClass->ObitTableReadRow = 
    (ObitTableReadRowFP)ObitTableSYReadRow;
  theClass->ObitTableWriteRow = 
    (ObitTableWriteRowFP)ObitTableSYWriteRow;

} /* end ObitTableSYClassDefFn */

/**
 * Get table specific information from the infolist or descriptor
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableSYUpdate (ObitTableSY *in, ObitErr *err)
{
  olong i;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM];
  ObitTableDesc *desc;
   

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Get Keywords */
   /* nIF */
  if (!ObitInfoListGet(in->myDesc->info, "NO_IF", &type, dim, 
		       (gpointer)&in->nIF, err)) return;
   /* nPol */
  if (!ObitInfoListGet(in->myDesc->info, "NO_POL", &type, dim, 
		       (gpointer)&in->nPol, err)) return;
   /* nAnt */
  if (!ObitInfoListGet(in->myDesc->info, "NO_ANT", &type, dim, 
		       (gpointer)&in->nAnt, err)) return;
   /* revision */
  in->revision = 1; 
  ObitInfoListGetTest(in->myDesc->info, "REVISION", &type, dim, 
		       (gpointer)&in->revision);

  /* initialize column numbers/offsets */
  in->TimeOff = -1;
  in->TimeCol = -1;
  in->TimeIOff = -1;
  in->TimeICol = -1;
  in->SourIDOff = -1;
  in->SourIDCol = -1;
  in->antennaNoOff = -1;
  in->antennaNoCol = -1;
  in->SubAOff = -1;
  in->SubACol = -1;
  in->FreqIDOff = -1;
  in->FreqIDCol = -1;
  in->PwrDif1Off = -1;
  in->PwrDif1Col = -1;
  in->PwrSum1Off = -1;
  in->PwrSum1Col = -1;
  in->Gain1Off = -1;
  in->Gain1Col = -1;
  in->PwrDif2Off = -1;
  in->PwrDif2Col = -1;
  in->PwrSum2Off = -1;
  in->PwrSum2Col = -1;
  in->Gain2Off = -1;
  in->Gain2Col = -1;
  /* Find columns and set offsets */
  desc = in->myDesc;
  if (desc->FieldName) {
    for (i=0; i<desc->nfield; i++) {
      if (!strncmp (desc->FieldName[i], "TIME    ", 8)) {
	 in->TimeOff = desc->offset[i];
 	 in->TimeCol = i;
      }
      if (!strncmp (desc->FieldName[i], "TIME INTERVAL ", 14)) {
	 in->TimeIOff = desc->offset[i];
 	 in->TimeICol = i;
      }
      if (!strncmp (desc->FieldName[i], "SOURCE ID  ", 11)) {
	 in->SourIDOff = desc->offset[i];
 	 in->SourIDCol = i;
      }
      if (!strncmp (desc->FieldName[i], "ANTENNA NO.", 11)) {
	 in->antennaNoOff = desc->offset[i];
 	 in->antennaNoCol = i;
      }
      if (!strncmp (desc->FieldName[i], "SUBARRAY   ", 11)) {
	 in->SubAOff = desc->offset[i];
 	 in->SubACol = i;
      }
      if (!strncmp (desc->FieldName[i], "FREQ ID  ", 9)) {
	 in->FreqIDOff = desc->offset[i];
 	 in->FreqIDCol = i;
      }
      if (!strncmp (desc->FieldName[i], "POWER DIF1", 10)) {
	     in->PwrDif1Off = desc->offset[i];
 	     in->PwrDif1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "POWER SUM1", 10)) {
	     in->PwrSum1Off = desc->offset[i];
 	     in->PwrSum1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "POST GAIN1", 10)) {
	     in->Gain1Off = desc->offset[i];
 	     in->Gain1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "POWER DIF2", 10)) {
	     in->PwrDif2Off = desc->offset[i];
 	     in->PwrDif2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "POWER SUM2", 10)) {
	     in->PwrSum2Off = desc->offset[i];
 	     in->PwrSum2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "POST GAIN2", 10)) {
	     in->Gain2Off = desc->offset[i];
 	     in->Gain2Col = i;
      }
     }
  }

  /* Check required columns */
  Obit_return_if_fail((in->TimeOff > -1), err,
       "ObitTableSYUpdate: Could not find column Time");
  Obit_return_if_fail((in->TimeIOff > -1), err,
       "ObitTableSYUpdate: Could not find column TimeI");
  Obit_return_if_fail((in->SourIDOff > -1), err,
       "ObitTableSYUpdate: Could not find column SourID");
  Obit_return_if_fail((in->antennaNoOff > -1), err,
       "ObitTableSYUpdate: Could not find column antennaNo");
  Obit_return_if_fail((in->SubAOff > -1), err,
       "ObitTableSYUpdate: Could not find column SubA");
  Obit_return_if_fail((in->FreqIDOff > -1), err,
       "ObitTableSYUpdate: Could not find column FreqID");
  if (in->nPol>=1) {
    Obit_return_if_fail((in->PwrDif1Off > -1), err,
       "ObitTableSYUpdate: Could not find column PwrDif1");
    Obit_return_if_fail((in->PwrSum1Off > -1), err,
       "ObitTableSYUpdate: Could not find column PwrSum1");
  } /* end of if index in range */
  if (in->nPol>=2) {
    Obit_return_if_fail((in->PwrDif2Off > -1), err,
       "ObitTableSYUpdate: Could not find column PwrDif2");
    Obit_return_if_fail((in->PwrSum2Off > -1), err,
       "ObitTableSYUpdate: Could not find column PwrSum2");
  } /* end of if index in range */
} /* end ObitTableSYUpdate */

/**
 * Copy table specific (keyword) information  to infolist.
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableSYDumpKey (ObitTableSY *in, ObitErr *err)
{
  ObitInfoList *info=NULL;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM] = {1,1,1,1,1};

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Set Keywords */
  if (in->myIO!=NULL) info = ((ObitTableDesc*)(in->myIO->myDesc))->info;
  else info = in->myDesc->info;
  /* nIF */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_IF", type, dim, 
		  (gpointer)&in->nIF);
  /* nPol */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_POL", type, dim, 
		  (gpointer)&in->nPol);
  /* nAnt */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_ANT", type, dim, 
		  (gpointer)&in->nAnt);
  /* revision */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "REVISION", type, dim, 
		  (gpointer)&in->revision);
   
} /* end ObitTableSYDumpKey */
