/**
 * Get table specific information from the infolist or descriptor
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableGBTVEGASACT_STATEUpdate (ObitTableGBTVEGASACT_STATE *in, ObitErr *err)
{
  olong i;
  ObitTableDesc *desc;
   

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Get Keywords */

  /* initialize column numbers/offsets */
  in->isigref1Off = -1;
  in->isigref1Col = -1;
  in->isigref2Off = -1;
  in->isigref2Col = -1;
  in->icalOff = -1;
  in->icalCol = -1;
  in->esigref1Off = -1;
  in->esigref1Col = -1;
  in->esigref2Off = -1;
  in->esigref2Col = -1;
  in->ecalOff = -1;
  in->ecalCol = -1;
  /* Find columns and set offsets */
  desc = in->myDesc;
  if (desc->FieldName) {
    for (i=0; i<desc->nfield; i++) {
      if (!strncmp (desc->FieldName[i], "ISIGREF1", 8)) {
	 in->isigref1Off = desc->offset[i];
 	 in->isigref1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "ISIGREF2", 8)) {
	 in->isigref2Off = desc->offset[i];
 	 in->isigref2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "ICAL   ", 7)) {
	 in->icalOff = desc->offset[i];
 	 in->icalCol = i;
      }
      if (!strncmp (desc->FieldName[i], "ESIGREF1", 8)) {
	 in->esigref1Off = desc->offset[i];
 	 in->esigref1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "ESIGREF2", 8)) {
	 in->esigref2Off = desc->offset[i];
 	 in->esigref2Col = i;
      }
      if (!strncmp (desc->FieldName[i], "ECAL    ", 8)) {
	 in->ecalOff = desc->offset[i];
 	 in->ecalCol = i;
      }
     }
  }

  Obit_return_if_fail((in->isigref1Off > -1), err,
       "ObitTableACT_STATEUpdate: Could not find column isigref1");
  Obit_return_if_fail((in->isigref2Off > -1), err,
       "ObitTableACT_STATEUpdate: Could not find column isigref2");
  Obit_return_if_fail((in->icalOff > -1), err,
       "ObitTableACT_STATEUpdate: Could not find column ical");
  Obit_return_if_fail((in->esigref1Off > -1), err,
       "ObitTableACT_STATEUpdate: Could not find column esigref1");
  Obit_return_if_fail((in->esigref2Off > -1), err,
       "ObitTableACT_STATEUpdate: Could not find column esigref2");
} /* end ObitTableGBTVEGASACT_STATEUpdate */

/**
 * Copy table specific (keyword) information  to infolist.
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableGBTVEGASACT_STATEDumpKey (ObitTableGBTVEGASACT_STATE *in, ObitErr *err)
{
  ObitInfoList *info=NULL;

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Set Keywords */
  if (in->myIO!=NULL) info = ((ObitTableDesc*)(in->myIO->myDesc))->info;
  else info = in->myDesc->info;
   
} /* end ObitTableGBTVEGASACT_STATEDumpKey */
