/**
 * Read a table row and return an easily digested version.
 * Scalar values are copied but for array values, pointers 
 * into the data array are returned.
 * \param in       Table to read
 * \param iDATARow   Row number, -1 -> next
 * \param row      Table Row structure to receive data
 * \param err ObitErr for reporting errors.
 * \return return code, OBIT_IO_OK=> OK
 */
ObitIOCode 
ObitTableGBTVEGASDATAReadRow  (ObitTableGBTVEGASDATA *in, olong iDATARow, ObitTableGBTVEGASDATARow *row,
		     ObitErr *err)
{
  ObitIOCode retCode = OBIT_IO_SpecErr;
  odouble   *dRow;
  oint      *iRow;
  gshort    *siRow;
  ollong    *llRow;
  ofloat    *fRow;
  gchar     *cRow;
  gboolean  *lRow;
  guint8    *bRow;
  gchar *routine = "ObitTableGBTVEGASDATAReadRow";
  
  /* error checks */
  g_assert (ObitErrIsA(err));
  if (err->error) return retCode;
  g_assert (ObitIsA(in, &myClassInfo));

  if (in->myStatus == OBIT_Inactive) {
    Obit_log_error(err, OBIT_Error,
		   "ObitTableGBTVEGASDATA Table is inactive for  %s ", in->name);
    return retCode;
 }

  /* read row iDATARow */
  retCode = ObitTableRead ((ObitTable*)in, iDATARow, NULL,  err);
  if (err->error) 
    Obit_traceback_val (err, routine, in->name, retCode);

  /* Typed pointers to row of data */  
  dRow  = (odouble*)in->buffer;
  iRow  = (oint*)in->buffer;
  siRow = (gshort*)in->buffer;
  llRow = (ollong*)in->buffer;
  fRow  = (ofloat*)in->buffer;
  cRow  = (gchar*)in->buffer;
  lRow  = (gboolean*)in->buffer;
  bRow  = (guint8*)in->buffer;
  
  /* Copy scalar fields, for arrays only set pointer*/
  row->dmjd = dRow[in->dmjdOff];
  row->utcdelta = dRow[in->utcdeltaOff];
  row->integnum = iRow[in->integnumOff];
  row->time_ctr = llRow[in->time_ctrOff];
  row->integrat = fRow + in->integratOff;
  row->data = fRow + in->dataOff;
  row->accumid = iRow + in->accumidOff;
  row->sttspec = iRow + in->sttspecOff;
  row->stpspec = iRow + in->stpspecOff;
  row->status = iRow[in->myDesc->statusOff];

  return retCode;
} /*  end ObitTableGBTVEGASDATAReadRow */

