/**
 * Get table specific information from the infolist or descriptor
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableGBTVEGASDATAUpdate (ObitTableGBTVEGASDATA *in, ObitErr *err)
{
  olong i;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM];
  ObitTableDesc *desc;
   union ObitInfoListEquiv InfoReal; 

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Get Keywords */
   /* swperint */
  if (!ObitInfoListGet(in->myDesc->info, "SWPERINT", &type, (gint32*)&dim, 
		       (gpointer)&in->swperint, err)) return;
   /* utcstart */
  if (!ObitInfoListGet(in->myDesc->info, "UTCSTART", &type, (gint32*)&dim, 
		       (gpointer)&InfoReal, err)) return;
  if (type==OBIT_double) in->utcstart = InfoReal.dbl;
  if (type==OBIT_float)  in->utcstart = InfoReal.flt;
   /* utdstart */
  if (!ObitInfoListGet(in->myDesc->info, "UTDSTART", &type, (gint32*)&dim, 
		       (gpointer)&in->utdstart, err)) return;
   /* duration */
  if (!ObitInfoListGet(in->myDesc->info, "DURATION", &type, (gint32*)&dim, 
		       (gpointer)&InfoReal, err)) return;
  if (type==OBIT_double) in->duration = InfoReal.dbl;
  if (type==OBIT_float)  in->duration = InfoReal.flt;
   /* tdesc2 */
  if (!ObitInfoListGet(in->myDesc->info, "TDESC2", &type, (gint32*)&dim, 
		       (gpointer)&in->tdesc2, err)) return;
   /* tdesc3 */
  if (!ObitInfoListGet(in->myDesc->info, "TDESC3", &type, (gint32*)&dim, 
		       (gpointer)&in->tdesc3, err)) return;

  /* initialize column numbers/offsets */
  in->dmjdOff = -1;
  in->dmjdCol = -1;
  in->integratOff = -1;
  in->integratCol = -1;
  in->dataOff = -1;
  in->dataCol = -1;
  in->utcdeltaOff = -1;
  in->utcdeltaCol = -1;
  in->integnumOff = -1;
  in->integnumCol = -1;
  in->accumidOff = -1;
  in->accumidCol = -1;
  in->sttspecOff = -1;
  in->sttspecCol = -1;
  in->stpspecOff = -1;
  in->stpspecCol = -1;
  in->time_ctrOff = -1;
  in->time_ctrCol = -1;
  /* Find columns and set offsets */
  desc = in->myDesc;
  if (desc->FieldName) {
    for (i=0; i<desc->nfield; i++) {
      if (!strncmp (desc->FieldName[i], "DMJD    ", 8)) {
	 in->dmjdOff = desc->offset[i];
 	 in->dmjdCol = i;
      }
      if (!strncmp (desc->FieldName[i], "INTEGRAT", 8)) {
	 in->integratOff = desc->offset[i];
 	 in->integratCol = i;
      }
      if (!strncmp (desc->FieldName[i], "DATA    ", 8)) {
	 in->dataOff = desc->offset[i];
 	 in->dataCol = i;
      }
      if (!strncmp (desc->FieldName[i], "UTCDELTA", 8)) {
	 in->utcdeltaOff = desc->offset[i];
 	 in->utcdeltaCol = i;
      }
      if (!strncmp (desc->FieldName[i], "INTEGNUM", 8)) {
	 in->integnumOff = desc->offset[i];
 	 in->integnumCol = i;
      }
      if (!strncmp (desc->FieldName[i], "ACCUMID ", 8)) {
	 in->accumidOff = desc->offset[i];
 	 in->accumidCol = i;
      }
      if (!strncmp (desc->FieldName[i], "STTSPEC ", 8)) {
	 in->sttspecOff = desc->offset[i];
 	 in->sttspecCol = i;
      }
      if (!strncmp (desc->FieldName[i], "STPSPEC ", 8)) {
	 in->stpspecOff = desc->offset[i];
 	 in->stpspecCol = i;
      }
      if (!strncmp (desc->FieldName[i], "TIME_CTR", 8)) {
	 in->time_ctrOff = desc->offset[i];
 	 in->time_ctrCol = i;
      }
     }
  }

  Obit_return_if_fail((in->dmjdOff > -1), err,
       "ObitTableDATAUpdate: Could not find column dmjd");
  Obit_return_if_fail((in->integratOff > -1), err,
       "ObitTableDATAUpdate: Could not find column integrat");
  Obit_return_if_fail((in->dataOff > -1), err,
       "ObitTableDATAUpdate: Could not find column data");
  Obit_return_if_fail((in->utcdeltaOff > -1), err,
       "ObitTableDATAUpdate: Could not find column utcdelta");
  Obit_return_if_fail((in->integnumOff > -1), err,
       "ObitTableDATAUpdate: Could not find column integnum");
  Obit_return_if_fail((in->accumidOff > -1), err,
       "ObitTableDATAUpdate: Could not find column accumid");
  Obit_return_if_fail((in->sttspecOff > -1), err,
       "ObitTableDATAUpdate: Could not find column sttspec");
  Obit_return_if_fail((in->stpspecOff > -1), err,
       "ObitTableDATAUpdate: Could not find column stpspec");
} /* end ObitTableGBTVEGASDATAUpdate */

/**
 * Copy table specific (keyword) information  to infolist.
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableGBTVEGASDATADumpKey (ObitTableGBTVEGASDATA *in, ObitErr *err)
{
  ObitInfoList *info=NULL;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM] = {1,1,1,1,1};

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Set Keywords */
  if (in->myIO!=NULL) info = ((ObitTableDesc*)(in->myIO->myDesc))->info;
  else info = in->myDesc->info;
  /* swperint */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "SWPERINT", type, (gint32*)&dim, 
		  (gpointer)&in->swperint);
  /* utcstart */
  type  = OBIT_double;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "UTCSTART", type, (gint32*)&dim, 
		  (gpointer)&in->utcstart);
  /* utdstart */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "UTDSTART", type, (gint32*)&dim, 
		  (gpointer)&in->utdstart);
  /* duration */
  type  = OBIT_float;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "DURATION", type, (gint32*)&dim, 
		  (gpointer)&in->duration);
  /* tdesc2 */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEDATA;
  ObitInfoListAlwaysPut(info, "TDESC2", type, (gint32*)&dim, 
		  (gpointer)&in->tdesc2);
  /* tdesc3 */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEDATA;
  ObitInfoListAlwaysPut(info, "TDESC3", type, (gint32*)&dim, 
		  (gpointer)&in->tdesc3);
   
} /* end ObitTableGBTVEGASDATADumpKey */
