/**
 * Get table specific information from the infolist or descriptor
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableGBTVEGASPORTUpdate (ObitTableGBTVEGASPORT *in, ObitErr *err)
{
  olong i;
  ObitTableDesc *desc;
   

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Get Keywords */

  /* initialize column numbers/offsets */
  in->bankOff = -1;
  in->bankCol = -1;
  in->portOff = -1;
  in->portCol = -1;
  in->measpwrOff = -1;
  in->measpwrCol = -1;
  in->toneOff = -1;
  in->toneCol = -1;
  /* Find columns and set offsets */
  desc = in->myDesc;
  if (desc->FieldName) {
    for (i=0; i<desc->nfield; i++) {
      if (!strncmp (desc->FieldName[i], "BANK", 4)) {
	 in->bankOff = desc->offset[i];
 	 in->bankCol = i;
      }
      if (!strncmp (desc->FieldName[i], "PORT", 4)) {
	 in->portOff = desc->offset[i];
 	 in->portCol = i;
      }
      if (!strncmp (desc->FieldName[i], "MEASPWR", 7)) {
	 in->measpwrOff = desc->offset[i];
 	 in->measpwrCol = i;
      }
      if (!strncmp (desc->FieldName[i], "T_N_SW", 6)) {
	 in->toneOff = desc->offset[i];
 	 in->toneCol = i;
      }
     }
  }

  Obit_return_if_fail((in->bankOff > -1), err,
       "ObitTablePORTUpdate: Could not find column bank");
  Obit_return_if_fail((in->portOff > -1), err,
       "ObitTablePORTUpdate: Could not find column port");
  Obit_return_if_fail((in->measpwrOff > -1), err,
       "ObitTablePORTUpdate: Could not find column measpwr");
} /* end ObitTableGBTVEGASPORTUpdate */

/**
 * Copy table specific (keyword) information  to infolist.
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableGBTVEGASPORTDumpKey (ObitTableGBTVEGASPORT *in, ObitErr *err)
{
  ObitInfoList *info=NULL;

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Set Keywords */
  if (in->myIO!=NULL) info = ((ObitTableDesc*)(in->myIO->myDesc))->info;
  else info = in->myDesc->info;
   
} /* end ObitTableGBTVEGASPORTDumpKey */
