/**
 * Read a table row and return an easily digested version.
 * Scalar values are copied but for array values, pointers 
 * into the data array are returned.
 * \param in       Table to read
 * \param iPORTRow   Row number, -1 -> next
 * \param row      Table Row structure to receive data
 * \param err ObitErr for reporting errors.
 * \return return code, OBIT_IO_OK=> OK
 */
ObitIOCode 
ObitTableGBTVEGASSAMPLERReadRow  (ObitTableGBTVEGASSAMPLER *in, olong iPORTRow, ObitTableGBTVEGASSAMPLERRow *row,
		     ObitErr *err)
{
  ObitIOCode retCode = OBIT_IO_SpecErr;
  odouble   *dRow;
  oint      *iRow;
  gshort    *siRow;
  ollong    *llRow;
  ofloat    *fRow;
  gchar     *cRow;
  gboolean  *lRow;
  guint8    *bRow;
  gchar *routine = "ObitTableGBTVEGASSAMPLERReadRow";
  
  /* error checks */
  g_assert (ObitErrIsA(err));
  if (err->error) return retCode;
  g_assert (ObitIsA(in, &myClassInfo));

  if (in->myStatus == OBIT_Inactive) {
    Obit_log_error(err, OBIT_Error,
		   "ObitTableGBTVEGASSAMPLER Table is inactive for  %s ", in->name);
    return retCode;
 }

  /* read row iPORTRow */
  retCode = ObitTableRead ((ObitTable*)in, iPORTRow, NULL,  err);
  if (err->error) 
    Obit_traceback_val (err, routine, in->name, retCode);

  /* Typed pointers to row of data */  
  dRow  = (odouble*)in->buffer;
  iRow  = (oint*)in->buffer;
  siRow = (gshort*)in->buffer;
  llRow = (ollong*)in->buffer;
  fRow  = (ofloat*)in->buffer;
  cRow  = (gchar*)in->buffer;
  lRow  = (gboolean*)in->buffer;
  bRow  = (guint8*)in->buffer;
  
  /* Copy scalar fields, for arrays only set pointer*/
  row->bank_a = cRow[in->bank_aOff];
  row->port_a = cRow[in->port_aOff];
  row->bank_b = cRow[in->bank_bOff];
  row->port_b = cRow[in->port_bOff];
  row->subband = siRow[in->subbandOff];
  row->crval1 = dRow[in->crval1Off];
  row->cdelt1 = dRow[in->cdelt1Off];
  row->freqres = dRow[in->freqresOff];
  row->datatype = cRow + in->datatypeOff;
  row->status = iRow[in->myDesc->statusOff];

  return retCode;
} /*  end ObitTableGBTVEGASSAMPLERReadRow */

