/**
 * Get table specific information from the infolist or descriptor
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableGBTVEGASSAMPLERUpdate (ObitTableGBTVEGASSAMPLER *in, ObitErr *err)
{
  olong i;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM];
  ObitTableDesc *desc;
   union ObitInfoListEquiv InfoReal; 

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Get Keywords */
   /* crpix1 */
  if (!ObitInfoListGet(in->myDesc->info, "CRPIX1", &type, (gint32*)&dim, 
		       (gpointer)&InfoReal, err)) return;
  if (type==OBIT_double) in->crpix1 = InfoReal.dbl;
  if (type==OBIT_float)  in->crpix1 = InfoReal.flt;
   /* polarize */
  if (!ObitInfoListGet(in->myDesc->info, "POLARIZE", &type, (gint32*)&dim, 
		       (gpointer)&in->polarize, err)) return;

  /* initialize column numbers/offsets */
  in->bank_aOff = -1;
  in->bank_aCol = -1;
  in->port_aOff = -1;
  in->port_aCol = -1;
  in->bank_bOff = -1;
  in->bank_bCol = -1;
  in->port_bOff = -1;
  in->port_bCol = -1;
  in->datatypeOff = -1;
  in->datatypeCol = -1;
  in->subbandOff = -1;
  in->subbandCol = -1;
  in->crval1Off = -1;
  in->crval1Col = -1;
  in->cdelt1Off = -1;
  in->cdelt1Col = -1;
  in->freqresOff = -1;
  in->freqresCol = -1;
  /* Find columns and set offsets */
  desc = in->myDesc;
  if (desc->FieldName) {
    for (i=0; i<desc->nfield; i++) {
      if (!strncmp (desc->FieldName[i], "BANK_A  ", 8)) {
	 in->bank_aOff = desc->offset[i];
 	 in->bank_aCol = i;
      }
      if (!strncmp (desc->FieldName[i], "PORT_A  ", 8)) {
	 in->port_aOff = desc->offset[i];
 	 in->port_aCol = i;
      }
      if (!strncmp (desc->FieldName[i], "BANK_B  ", 8)) {
	 in->bank_bOff = desc->offset[i];
 	 in->bank_bCol = i;
      }
      if (!strncmp (desc->FieldName[i], "PORT_B  ", 8)) {
	 in->port_bOff = desc->offset[i];
 	 in->port_bCol = i;
      }
      if (!strncmp (desc->FieldName[i], "DATATYPE", 8)) {
	 in->datatypeOff = desc->offset[i];
 	 in->datatypeCol = i;
      }
      if (!strncmp (desc->FieldName[i], "SUBBAND ", 8)) {
	 in->subbandOff = desc->offset[i];
 	 in->subbandCol = i;
      }
      if (!strncmp (desc->FieldName[i], "CRVAL1  ", 8)) {
	 in->crval1Off = desc->offset[i];
 	 in->crval1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "CDELT1  ", 8)) {
	 in->cdelt1Off = desc->offset[i];
 	 in->cdelt1Col = i;
      }
      if (!strncmp (desc->FieldName[i], "FREQRES ", 8)) {
	 in->freqresOff = desc->offset[i];
 	 in->freqresCol = i;
      }
     }
  }

  Obit_return_if_fail((in->bank_aOff > -1), err,
       "ObitTablePORTUpdate: Could not find column bank_a");
  Obit_return_if_fail((in->port_aOff > -1), err,
       "ObitTablePORTUpdate: Could not find column port_a");
  Obit_return_if_fail((in->bank_bOff > -1), err,
       "ObitTablePORTUpdate: Could not find column bank_b");
  Obit_return_if_fail((in->port_bOff > -1), err,
       "ObitTablePORTUpdate: Could not find column port_b");
  Obit_return_if_fail((in->datatypeOff > -1), err,
       "ObitTablePORTUpdate: Could not find column datatype");
  Obit_return_if_fail((in->subbandOff > -1), err,
       "ObitTablePORTUpdate: Could not find column subband");
  Obit_return_if_fail((in->crval1Off > -1), err,
       "ObitTablePORTUpdate: Could not find column crval1");
  Obit_return_if_fail((in->cdelt1Off > -1), err,
       "ObitTablePORTUpdate: Could not find column cdelt1");
} /* end ObitTableGBTVEGASSAMPLERUpdate */

/**
 * Copy table specific (keyword) information  to infolist.
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableGBTVEGASSAMPLERDumpKey (ObitTableGBTVEGASSAMPLER *in, ObitErr *err)
{
  ObitInfoList *info=NULL;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM] = {1,1,1,1,1};

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Set Keywords */
  if (in->myIO!=NULL) info = ((ObitTableDesc*)(in->myIO->myDesc))->info;
  else info = in->myDesc->info;
  /* crpix1 */
  type  = OBIT_float;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "CRPIX1", type, (gint32*)&dim, 
		  (gpointer)&in->crpix1);
  /* polarize */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEPORT;
  ObitInfoListAlwaysPut(info, "POLARIZE", type, (gint32*)&dim, 
		  (gpointer)&in->polarize);
   
} /* end ObitTableGBTVEGASSAMPLERDumpKey */
