/**
 * Get table specific information from the infolist or descriptor
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableOTFCalUpdate (ObitTableOTFCal *in, ObitErr *err)
{
  olong i;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM];
  ObitTableDesc *desc;
   

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Get Keywords */
   /* numDet */
  in->numDet = 1; 
  ObitInfoListGetTest(in->myDesc->info, "NO_DETEC", &type, (gint32*)&dim, 
		       (gpointer)&in->numDet);
   /* numPoly */
  in->numPoly = 1; 
  ObitInfoListGetTest(in->myDesc->info, "NO_POLY", &type, (gint32*)&dim, 
		       (gpointer)&in->numPoly);

  /* initialize column numbers/offsets */
  in->TimeOff = -1;
  in->TimeCol = -1;
  in->TimeIOff = -1;
  in->TimeICol = -1;
  in->TargetOff = -1;
  in->TargetCol = -1;
  in->dAzOff = -1;
  in->dAzCol = -1;
  in->dElOff = -1;
  in->dElCol = -1;
  in->calOff = -1;
  in->calCol = -1;
  in->addOff = -1;
  in->addCol = -1;
  in->multOff = -1;
  in->multCol = -1;
  in->wtOff = -1;
  in->wtCol = -1;
  in->polyOff = -1;
  in->polyCol = -1;
  /* Find columns and set offsets */
  desc = in->myDesc;
  if (desc->FieldName) {
    for (i=0; i<desc->nfield; i++) {
      if (!strncmp (desc->FieldName[i], "TIME    ", 8)) {
	 in->TimeOff = desc->offset[i];
 	 in->TimeCol = i;
      }
      if (!strncmp (desc->FieldName[i], "TIME_INT", 8)) {
	 in->TimeIOff = desc->offset[i];
 	 in->TimeICol = i;
      }
      if (!strncmp (desc->FieldName[i], "TARGET", 6)) {
	 in->TargetOff = desc->offset[i];
 	 in->TargetCol = i;
      }
      if (!strncmp (desc->FieldName[i], "DELTA_AZ", 8)) {
	 in->dAzOff = desc->offset[i];
 	 in->dAzCol = i;
      }
      if (!strncmp (desc->FieldName[i], "DELTA_El", 8)) {
	 in->dElOff = desc->offset[i];
 	 in->dElCol = i;
      }
      if (!strncmp (desc->FieldName[i], "CAL     ", 8)) {
	 in->calOff = desc->offset[i];
 	 in->calCol = i;
      }
      if (!strncmp (desc->FieldName[i], "ADD     ", 8)) {
	 in->addOff = desc->offset[i];
 	 in->addCol = i;
      }
      if (!strncmp (desc->FieldName[i], "MULT    ", 8)) {
	 in->multOff = desc->offset[i];
 	 in->multCol = i;
      }
      if (!strncmp (desc->FieldName[i], "WEIGHT  ", 8)) {
	 in->wtOff = desc->offset[i];
 	 in->wtCol = i;
      }
      if (!strncmp (desc->FieldName[i], "POLY    ", 8)) {
	 in->polyOff = desc->offset[i];
 	 in->polyCol = i;
      }
     }
  }

  Obit_return_if_fail((in->TimeOff > -1), err,
       "ObitTableOTFCalUpdate: Could not find column Time");
  Obit_return_if_fail((in->TimeIOff > -1), err,
       "ObitTableOTFCalUpdate: Could not find column TimeI");
  Obit_return_if_fail((in->TargetOff > -1), err,
       "ObitTableOTFCalUpdate: Could not find column Target");
  Obit_return_if_fail((in->dAzOff > -1), err,
       "ObitTableOTFCalUpdate: Could not find column dAz");
  Obit_return_if_fail((in->dElOff > -1), err,
       "ObitTableOTFCalUpdate: Could not find column dEl");
  Obit_return_if_fail((in->calOff > -1), err,
       "ObitTableOTFCalUpdate: Could not find column cal");
  Obit_return_if_fail((in->addOff > -1), err,
       "ObitTableOTFCalUpdate: Could not find column add");
  Obit_return_if_fail((in->multOff > -1), err,
       "ObitTableOTFCalUpdate: Could not find column mult");
  Obit_return_if_fail((in->wtOff > -1), err,
       "ObitTableOTFCalUpdate: Could not find column wt");
} /* end ObitTableOTFCalUpdate */

/**
 * Copy table specific (keyword) information  to infolist.
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableOTFCalDumpKey (ObitTableOTFCal *in, ObitErr *err)
{
  ObitInfoList *info=NULL;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM] = {1,1,1,1,1};

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Set Keywords */
  if (in->myIO!=NULL) info = ((ObitTableDesc*)(in->myIO->myDesc))->info;
  else info = in->myDesc->info;
  /* numDet */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_DETEC", type, (gint32*)&dim, 
		  (gpointer)&in->numDet);
  /* numPoly */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_POLY", type, (gint32*)&dim, 
		  (gpointer)&in->numPoly);
   
} /* end ObitTableOTFCalDumpKey */
