/**
 * Get table specific information from the infolist or descriptor
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableOTFIndexUpdate (ObitTableOTFIndex *in, ObitErr *err)
{
  olong i;
  ObitTableDesc *desc;
   

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Get Keywords */

  /* initialize column numbers/offsets */
  in->ScanIDOff = -1;
  in->ScanIDCol = -1;
  in->TimeOff = -1;
  in->TimeCol = -1;
  in->TimeIOff = -1;
  in->TimeICol = -1;
  in->TargetIDOff = -1;
  in->TargetIDCol = -1;
  in->StartRecOff = -1;
  in->StartRecCol = -1;
  in->EndRecOff = -1;
  in->EndRecCol = -1;
  /* Find columns and set offsets */
  desc = in->myDesc;
  if (desc->FieldName) {
    for (i=0; i<desc->nfield; i++) {
      if (!strncmp (desc->FieldName[i], "SCAN_ID ", 8)) {
	 in->ScanIDOff = desc->offset[i];
 	 in->ScanIDCol = i;
      }
      if (!strncmp (desc->FieldName[i], "TIME    ", 8)) {
	 in->TimeOff = desc->offset[i];
 	 in->TimeCol = i;
      }
      if (!strncmp (desc->FieldName[i], "TIME_INTERVAL ", 14)) {
	 in->TimeIOff = desc->offset[i];
 	 in->TimeICol = i;
      }
      if (!strncmp (desc->FieldName[i], "TARGET_ID ", 10)) {
	 in->TargetIDOff = desc->offset[i];
 	 in->TargetIDCol = i;
      }
      if (!strncmp (desc->FieldName[i], "START_REC ", 10)) {
	 in->StartRecOff = desc->offset[i];
 	 in->StartRecCol = i;
      }
      if (!strncmp (desc->FieldName[i], "END_REC  ", 9)) {
	 in->EndRecOff = desc->offset[i];
 	 in->EndRecCol = i;
      }
     }
  }

  Obit_return_if_fail((in->ScanIDOff > -1), err,
       "ObitTableOTFIndexUpdate: Could not find column ScanID");
  Obit_return_if_fail((in->TimeOff > -1), err,
       "ObitTableOTFIndexUpdate: Could not find column Time");
  Obit_return_if_fail((in->TimeIOff > -1), err,
       "ObitTableOTFIndexUpdate: Could not find column TimeI");
  Obit_return_if_fail((in->TargetIDOff > -1), err,
       "ObitTableOTFIndexUpdate: Could not find column TargetID");
  Obit_return_if_fail((in->StartRecOff > -1), err,
       "ObitTableOTFIndexUpdate: Could not find column StartRec");
} /* end ObitTableOTFIndexUpdate */

/**
 * Copy table specific (keyword) information  to infolist.
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableOTFIndexDumpKey (ObitTableOTFIndex *in, ObitErr *err)
{
  ObitInfoList *info=NULL;

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Set Keywords */
  if (in->myIO!=NULL) info = ((ObitTableDesc*)(in->myIO->myDesc))->info;
  else info = in->myDesc->info;
   
} /* end ObitTableOTFIndexDumpKey */
