/**
 * Get table specific information from the infolist or descriptor
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableOTFScanDataUpdate (ObitTableOTFScanData *in, ObitErr *err)
{
  olong i;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM];
  ObitTableDesc *desc;
   union ObitInfoListEquiv InfoReal; 

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Get Keywords */
   /* numDet */
  if (!ObitInfoListGet(in->myDesc->info, "NO_DETEC", &type, (gint32*)&dim, 
		       (gpointer)&in->numDet, err)) return;
   /* origin */
  if (!ObitInfoListGet(in->myDesc->info, "ORIGIN", &type, (gint32*)&dim, 
		       (gpointer)&in->origin, err)) return;
   /* object */
  if (!ObitInfoListGet(in->myDesc->info, "OBJECT", &type, (gint32*)&dim, 
		       (gpointer)&in->object, err)) return;
   /* teles */
  if (!ObitInfoListGet(in->myDesc->info, "TELESCOP", &type, (gint32*)&dim, 
		       (gpointer)&in->teles, err)) return;
   /* obsdat */
  if (!ObitInfoListGet(in->myDesc->info, "DATE-OBS", &type, (gint32*)&dim, 
		       (gpointer)&in->obsdat, err)) return;
   /* epoch */
  if (!ObitInfoListGet(in->myDesc->info, "EPOCH", &type, (gint32*)&dim, 
		       (gpointer)&InfoReal, err)) return;
  if (type==OBIT_double) in->epoch = InfoReal.dbl;
  if (type==OBIT_float)  in->epoch = InfoReal.flt;
   /* bunit */
  if (!ObitInfoListGet(in->myDesc->info, "BUNIT", &type, (gint32*)&dim, 
		       (gpointer)&in->bunit, err)) return;
   /* obsra */
  if (!ObitInfoListGet(in->myDesc->info, "OBSRA", &type, (gint32*)&dim, 
		       (gpointer)&InfoReal, err)) return;
  if (type==OBIT_double) in->obsra = InfoReal.dbl;
  if (type==OBIT_float)  in->obsra = InfoReal.flt;
   /* obsdec */
  if (!ObitInfoListGet(in->myDesc->info, "OBSDEC", &type, (gint32*)&dim, 
		       (gpointer)&InfoReal, err)) return;
  if (type==OBIT_double) in->obsdec = InfoReal.dbl;
  if (type==OBIT_float)  in->obsdec = InfoReal.flt;
  in->beamSize = 0.00111; 
  InfoReal.dbl = 0.00111; 
  type = OBIT_double;
  ObitInfoListGetTest(in->myDesc->info, "BEAMSIZE", &type, (gint32*)&dim, 
		       (gpointer)&InfoReal);
  if (type==OBIT_double) in->beamSize = InfoReal.dbl;
  if (type==OBIT_float)  in->beamSize = InfoReal.flt;
  in->diameter = 100.0; 
  InfoReal.dbl = 100.0; 
  type = OBIT_double;
  ObitInfoListGetTest(in->myDesc->info, "DIAMETER", &type, (gint32*)&dim, 
		       (gpointer)&InfoReal);
  if (type==OBIT_double) in->diameter = InfoReal.dbl;
  if (type==OBIT_float)  in->diameter = InfoReal.flt;
   /* OTFType */
  strncpy (in->OTFType, "Unknown", MAXKEYCHARTABLEOTFScanData); 
  ObitInfoListGetTest(in->myDesc->info, "OTFTYPE", &type, (gint32*)&dim, 
		       (gpointer)&in->OTFType);

  /* initialize column numbers/offsets */
  in->TimeOff = -1;
  in->TimeCol = -1;
  in->TimeIOff = -1;
  in->TimeICol = -1;
  in->TargetOff = -1;
  in->TargetCol = -1;
  in->ScanOff = -1;
  in->ScanCol = -1;
  in->RAOff = -1;
  in->RACol = -1;
  in->DecOff = -1;
  in->DecCol = -1;
  in->rotateOff = -1;
  in->rotateCol = -1;
  in->calOff = -1;
  in->calCol = -1;
  in->dataOff = -1;
  in->dataCol = -1;
  /* Find columns and set offsets */
  desc = in->myDesc;
  if (desc->FieldName) {
    for (i=0; i<desc->nfield; i++) {
      if (!strncmp (desc->FieldName[i], "TIME    ", 8)) {
	 in->TimeOff = desc->offset[i];
 	 in->TimeCol = i;
      }
      if (!strncmp (desc->FieldName[i], "TIME_INT", 8)) {
	 in->TimeIOff = desc->offset[i];
 	 in->TimeICol = i;
      }
      if (!strncmp (desc->FieldName[i], "TARGET", 6)) {
	 in->TargetOff = desc->offset[i];
 	 in->TargetCol = i;
      }
      if (!strncmp (desc->FieldName[i], "Scan", 4)) {
	 in->ScanOff = desc->offset[i];
 	 in->ScanCol = i;
      }
      if (!strncmp (desc->FieldName[i], "RA  ", 4)) {
	 in->RAOff = desc->offset[i];
 	 in->RACol = i;
      }
      if (!strncmp (desc->FieldName[i], "DEC    ", 7)) {
	 in->DecOff = desc->offset[i];
 	 in->DecCol = i;
      }
      if (!strncmp (desc->FieldName[i], "ROTATE  ", 8)) {
	 in->rotateOff = desc->offset[i];
 	 in->rotateCol = i;
      }
      if (!strncmp (desc->FieldName[i], "CAL    ", 7)) {
	 in->calOff = desc->offset[i];
 	 in->calCol = i;
      }
      if (!strncmp (desc->FieldName[i], "DATA    ", 8)) {
	 in->dataOff = desc->offset[i];
 	 in->dataCol = i;
      }
     }
  }

  Obit_return_if_fail((in->TimeOff > -1), err,
       "ObitTableOTFScanDataUpdate: Could not find column Time");
  Obit_return_if_fail((in->TimeIOff > -1), err,
       "ObitTableOTFScanDataUpdate: Could not find column TimeI");
  Obit_return_if_fail((in->TargetOff > -1), err,
       "ObitTableOTFScanDataUpdate: Could not find column Target");
  Obit_return_if_fail((in->ScanOff > -1), err,
       "ObitTableOTFScanDataUpdate: Could not find column Scan");
  Obit_return_if_fail((in->RAOff > -1), err,
       "ObitTableOTFScanDataUpdate: Could not find column RA");
  Obit_return_if_fail((in->DecOff > -1), err,
       "ObitTableOTFScanDataUpdate: Could not find column Dec");
  Obit_return_if_fail((in->rotateOff > -1), err,
       "ObitTableOTFScanDataUpdate: Could not find column rotate");
  Obit_return_if_fail((in->calOff > -1), err,
       "ObitTableOTFScanDataUpdate: Could not find column cal");
} /* end ObitTableOTFScanDataUpdate */

/**
 * Copy table specific (keyword) information  to infolist.
 * \param info Table to update
 * \param err  ObitErr for reporting errors.
 */
static void ObitTableOTFScanDataDumpKey (ObitTableOTFScanData *in, ObitErr *err)
{
  ObitInfoList *info=NULL;
  ObitInfoType type;
  gint32 dim[MAXINFOELEMDIM] = {1,1,1,1,1};

 /* error checks */
   g_assert(ObitErrIsA(err));
  if (err->error) return;
  g_assert (ObitIsA(in, &myClassInfo));

  /* Set Keywords */
  if (in->myIO!=NULL) info = ((ObitTableDesc*)(in->myIO->myDesc))->info;
  else info = in->myDesc->info;
  /* numDet */
  type  = OBIT_oint;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "NO_DETEC", type, (gint32*)&dim, 
		  (gpointer)&in->numDet);
  /* origin */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEOTFScanData;
  ObitInfoListAlwaysPut(info, "ORIGIN", type, (gint32*)&dim, 
		  (gpointer)&in->origin);
  /* object */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEOTFScanData;
  ObitInfoListAlwaysPut(info, "OBJECT", type, (gint32*)&dim, 
		  (gpointer)&in->object);
  /* teles */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEOTFScanData;
  ObitInfoListAlwaysPut(info, "TELESCOP", type, (gint32*)&dim, 
		  (gpointer)&in->teles);
  /* obsdat */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEOTFScanData;
  ObitInfoListAlwaysPut(info, "DATE-OBS", type, (gint32*)&dim, 
		  (gpointer)&in->obsdat);
  /* epoch */
  type  = OBIT_float;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "EPOCH", type, (gint32*)&dim, 
		  (gpointer)&in->epoch);
  /* bunit */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEOTFScanData;
  ObitInfoListAlwaysPut(info, "BUNIT", type, (gint32*)&dim, 
		  (gpointer)&in->bunit);
  /* obsra */
  type  = OBIT_double;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "OBSRA", type, (gint32*)&dim, 
		  (gpointer)&in->obsra);
  /* obsdec */
  type  = OBIT_double;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "OBSDEC", type, (gint32*)&dim, 
		  (gpointer)&in->obsdec);
  /* beamSize */
  type  = OBIT_float;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "BEAMSIZE", type, (gint32*)&dim, 
		  (gpointer)&in->beamSize);
  /* diameter */
  type  = OBIT_float;
  dim[0] = 1;
  ObitInfoListAlwaysPut(info, "DIAMETER", type, (gint32*)&dim, 
		  (gpointer)&in->diameter);
  /* OTFType */
  type  = OBIT_string;
  dim[0] = MAXKEYCHARTABLEOTFScanData;
  ObitInfoListAlwaysPut(info, "OTFTYPE", type, (gint32*)&dim, 
		  (gpointer)&in->OTFType);
   
} /* end ObitTableOTFScanDataDumpKey */
